INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586', 2, 'Cunning Madola', 'assets/topic/asb/19552.png', 'Mariam Yusuf', 'FFCFB5DD');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_3', 3, NULL, 'assets/topic/asb/19543.png', 'There was a rich cunning man called Madola. He hired people who worked hard but he did not pay them their wages.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_4', 3, NULL, 'assets/topic/asb/19544.png', 'Dande was 11 years old. He went to Madola for work so he could buy medicine for his grandmother. Madola hired Dande to graze his animals.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_5', 3, NULL, 'assets/topic/asb/19545.png', 'At the end of the month,  Dande went for his pay. The cunning Madola said,  "Go home and come back tomorrow for your pay."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_6', 3, NULL, 'assets/topic/asb/19546.png', 'Dande arrived early at Madola''s home expecting to be paid. Madola said,  "Why! Are you a student? Why so early? Is this a school?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_7', 3, NULL, 'assets/topic/asb/19547.png', 'Madola took Dande away from other workers. He said,  "I will pay you only if you bring me a gourd of honey."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_8', 3, NULL, 'assets/topic/asb/19548.png', 'Dande thought,  "I need the money for grandmother''s medicine." He ran to the forest and placed two gourds beneath a bee-hive to fill up.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_9', 3, NULL, 'assets/topic/asb/19549.png', 'While Dande wondered how to punish Madola,  a small black poisonous snake entered one of the gourds unnoticed. Afterwards,  Dande sealed the gourds and left.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_10', 3, NULL, 'assets/topic/asb/19550.png', 'Dande gave Madola one gourd expecting his pay. After tasting,  Madola said,  "Bring me another gourd and I will pay you." Dande ran for it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_11', 3, NULL, 'assets/topic/asb/19551.png', 'Dande brought the second gourd. He gave it to Madola saying,  "Here is another gourd of sweet honey like the last one. Pay me now."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_12', 3, NULL, 'assets/topic/asb/19552.png', 'Madola took the gourd but said in a cunning way,  "There is no pay today. Wait for the end of the year." Dande was angry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_13', 3, NULL, 'assets/topic/asb/19553.png', 'Madola wanted to eat the honey. He put his finger in the gourd and was bitten. He cried,  "I''m bitten! Help!" The snake disappeared unnoticed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_14', 3, NULL, 'assets/topic/asb/19554.png', 'All the workers ran away not knowing what had happened to their employer. A short while later,  an ambulance arrived to take Madola to hospital.', NULL);
