INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412', 2, 'Maguru gives legs', 'assets/topic/asb/13910.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_3', 3, NULL, 'assets/topic/asb/13904.png', 'Long time ago, animals had no legs.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_4', 3, NULL, 'assets/topic/asb/13905.png', 'Only people had legs, which Maguru gave to them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_5', 3, NULL, 'assets/topic/asb/13906.png', 'One day, Maguru decided to give legs to every animal. He wanted them to walk.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_6', 3, NULL, 'assets/topic/asb/13907.png', 'The animals said, "It will be very nice to have legs."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_7', 3, NULL, 'assets/topic/asb/13908.png', 'The animals said it was hard to crawl.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_8', 3, NULL, 'assets/topic/asb/13909.png', 'When the day came, many animals went to Maguru''s house.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_9', 3, NULL, 'assets/topic/asb/13910.png', 'Each animal was given four legs.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_10', 3, NULL, 'assets/topic/asb/13911.png', 'Everything was different after they got legs.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_11', 3, NULL, 'assets/topic/asb/13912.png', 'They went to show people.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_12', 3, NULL, 'assets/topic/asb/13913.png', 'The last one in the line was Millipede. Maguru asked,  "Is there anyone else after you?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_13', 3, NULL, 'assets/topic/asb/13914.png', 'Maguru thought, "He is the last one. What will I do with the rest of the legs?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_14', 3, NULL, 'assets/topic/asb/13915.png', 'Millipede was very happy. He said, "I will move faster than others."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_15', 3, NULL, 'assets/topic/asb/13916.png', 'After Millipede left, Snake came to Maguru''s house. He said to Maguru, "Please give me some legs."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_16', 3, NULL, 'assets/topic/asb/13917.png', 'Maguru said, "I have given out all the legs. Where were you?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_17', 3, NULL, 'assets/topic/asb/13918.png', 'Maguru looked to see if there were legs remaining.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_18', 3, NULL, 'assets/topic/asb/13919.png', 'Maguru said to Snake, "Sorry. There are no legs left." Snake crawled home without legs.
