INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405', 2, 'The sprouting bean', 'assets/topic/asb/1824.png', 'Clare Verbeek
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_3', 3, NULL, 'assets/topic/asb/1826.png', 'The root grows first.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_4', 3, NULL, 'assets/topic/asb/1827.png', 'It grows down into the soil.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_5', 3, NULL, 'assets/topic/asb/1828.png', 'Then the stem grows. It grows up towards the sun.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_6', 3, NULL, 'assets/topic/asb/1829.png', 'The first leaves come out.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_7', 3, NULL, 'assets/topic/asb/1824.png', 'The bean plant grows tall.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_8', 3, NULL, 'assets/topic/asb/1832.png', 'Then the flowers come out. The flowers turn into beans.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2405_9', 3, NULL, 'assets/topic/asb/1833.png', 'There are lots of seeds in the bean.', NULL);
