INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126', 2, 'An egg for bride wealth', 'assets/topic/asb/6733.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_3', 3, NULL, 'assets/topic/asb/6733.png', 'Once upon a time,  there was an old woman who loved her grandson very much. She gave him an egg as a gift. She said,  "From this egg you will get bride wealth for your marriage when you are of age."  And the boy went on his way.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_4', 3, NULL, 'assets/topic/asb/6732.png', 'First he met some blackberry gatherers who asked him for the egg. They used it to shoot at the berries and it broke.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_5', 3, NULL, 'assets/topic/asb/6731.png', 'The boy cried,  "EiEiEi! You have caused trouble for me! The egg was not mine. It belonged to my grandmother. And grandmother didn''t give it to me for nothing. She gave it to me to get bride wealth."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_6', 3, NULL, 'assets/topic/asb/6734.png', 'So the berry gatherers gave him the stick they used to bring down the berries. And the boy walked on.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_7', 3, NULL, 'assets/topic/asb/6735.png', 'Next the boy met some builders who asked him for his stick. They used it to build a house and it broke into pieces.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_8', 3, NULL, 'assets/topic/asb/6742.png', 'The boy cried,  "HeeHeeHee! You have caused trouble for me! The stick was not mine,  it belonged to the berry gatherers. And they didn''t give it to me for nothing. They gave it to me in place of my egg. The egg was not mine. It belonged to my grandmother. And grandmother didn''t give it to me for nothing,  She gave it to me to get bride wealth."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_9', 3, NULL, 'assets/topic/asb/6737.png', 'So the builders gave him a bundle of grass for thatching. And the boy walked on.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_10', 3, NULL, 'assets/topic/asb/6736.png', 'Next the boy found a herdsman who asked for the boy''s bundle of grass. The herdsman gave it to the cow and the cow ate it all up.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_11', 3, NULL, 'assets/topic/asb/6738.png', 'The boy cried,  "EEE! You have caused trouble for me! The grass was not mine. It belonged to the builders. The builders did not give it to me for nothing. They broke my stick. The stick was not mine. It belonged to the berry gatherers. And they didn''t give it to me for nothing. They gave it to me in place of my egg. The egg was not mine. It belonged to my grandmother. And grandmother did not give it to me for nothing. She gave it to me to get bride wealth."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_12', 3, NULL, 'assets/topic/asb/6739.png', 'So the herdsman gave the boy his cow. And the boy walked on.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_13', 3, NULL, 'assets/topic/asb/6743.png', 'While on his way home,  the boy came across a marriage celebration. The bride''s family asked him for his cow. He gave it to them. And they slaughtered it and ate it all.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_14', 3, NULL, 'assets/topic/asb/6740.png', 'The boy cried,  "EiEiEi! You have caused trouble for me! The cow was not mine. It belonged to the herdsman. The herdsman didn''t give it to me for nothing. He gave it to me in place of my bundle of grass. The grass was not mine. It belonged to the builders. The builders didn''t give it to me for nothing. They broke my stick. The stick was not mine. It belonged to the berry gatherers. They didn''t give it to me for nothing. They gave it to me in place of my egg. The egg was not mine. It belonged to my grandmother. And grandmother didn''t give it to me for nothing,  She gave it to me to get bride wealth."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21126_15', 3, NULL, 'assets/topic/asb/6741.png', 'Then the bride''s family said,  "We have nothing left to pay you. But let us give you the bride." And so,  the saying came to pass that,  "What goes round comes around!"', NULL);
