INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652', 2, 'My mother planted', 'assets/topic/asb/9002.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_3', 3, NULL, 'assets/topic/asb/8998.png', 'My mother planted. She planted a small garden. She planted a small garden of peas.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_4', 3, NULL, 'assets/topic/asb/8999.png', 'My father planted. He planted a small garden. He planted a small garden of flowers.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_5', 3, NULL, 'assets/topic/asb/9000.png', 'My mother weeded in both gardens.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_6', 3, NULL, 'assets/topic/asb/9001.png', 'My father watered in both gardens.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_7', 3, NULL, 'assets/topic/asb/9002.png', 'My parents and I worked in the small gardens. My sister and I harvested the flowers and peas.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_8', 3, NULL, 'assets/topic/asb/9003.png', 'The small garden of peas yielded sacks of peas. The small garden of flowers yielded sacks of flowers.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_9', 3, NULL, 'assets/topic/asb/9004.png', 'My mother planted. She planted a large garden. She planted a large garden of peas.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12652_10', 3, NULL, 'assets/topic/asb/9005.png', 'My father planted. He planted a large garden. He planted a large garden of flowers.', NULL);
