INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087', 2, 'Crocodile waits for brains', 'assets/topic/asb/25465.png', 'Northern Cape Teacher''s Workshop 2016
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_3', 3, NULL, 'assets/topic/asb/25460.png', 'Once upon a time, there was a crocodile living at a river with her young ones.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_4', 3, NULL, 'assets/topic/asb/25462.png', 'The crocodile taught her young ones not to trust humans.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_5', 3, NULL, 'assets/topic/asb/25461.png', 'One day a certain man crossed the river.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_6', 3, NULL, 'assets/topic/asb/25463.png', 'The man met the crocodile in the middle of the river and she attacked him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_7', 3, NULL, 'assets/topic/asb/25464.png', 'Just as the crocodile was about to kill him, she remembered her father''s words.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_8', 3, NULL, 'assets/topic/asb/25467.png', 'The man promised the crocodile that he could bring her human brains.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_9', 3, NULL, 'assets/topic/asb/25465.png', 'The crocodile and her young ones are still waiting on the riverbank to receive those brains.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22087_10', 3, NULL, 'assets/topic/asb/25466.png', 'Crocodiles are angry to this day because they never received the brains that were promised to them.', NULL);
