INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151', 2, 'Dog and Crocodile', 'assets/topic/asb/17149.png', 'Candiru Enzikuru Mary
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_3', 3, NULL, 'assets/topic/asb/17139.png', 'One day at the river,  Dog came across some eggs in the sand. He asked himself,  "Which animal left these eggs here?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_4', 3, NULL, 'assets/topic/asb/17140.png', 'He counted ten eggs and thought they might belong to Duck. He put them in his bag.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_5', 3, NULL, 'assets/topic/asb/17141.png', 'He took the eggs home and put them in a warm place.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_6', 3, NULL, 'assets/topic/asb/17142.png', 'When Dog returned to the river,  he met Crocodile. She asked,  "Have you seen my eggs?" "I don''t know anything about your eggs," he replied.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_7', 3, NULL, 'assets/topic/asb/17143.png', 'Crocodile started visiting all the animals asking about her eggs.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_8', 3, NULL, 'assets/topic/asb/17144.png', 'Meanwhile,  the eggs hatched one by one.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_9', 3, NULL, 'assets/topic/asb/17145.png', 'Dog did his best to look after the baby crocodiles,  but there was never enough food. The young crocodiles were always hungry.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_10', 3, NULL, 'assets/topic/asb/17146.png', 'One day Crocodile went to Dog''s house looking for her eggs. As she stood outside,  she heard the rumbling of little crocodile bellies!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_11', 3, NULL, 'assets/topic/asb/17147.png', 'Crocodile stormed inside and hit Dog with her tail. Dog yelped and jumped through the window.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_12', 3, NULL, 'assets/topic/asb/17148.png', 'Crocodile chased Dog all the way to the river.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_13', 3, NULL, 'assets/topic/asb/17149.png', 'Dog cried,  "I''m sorry,  I didn''t know they were your eggs!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21151_14', 3, NULL, 'assets/topic/asb/17150.png', 'Crocodile believed him and forgave him. She took her offspring for their first swimming lesson.', NULL);
