INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404', 2, 'Two little friends', 'assets/topic/asb/18402.png', 'Belainesh Woubishet
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_3', 3, NULL, 'assets/topic/asb/18401.png', 'There was Little Goat and Little Leopard. They became friends.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_4', 3, NULL, 'assets/topic/asb/18402.png', 'They went to a field together to play.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_5', 3, NULL, 'assets/topic/asb/18403.png', 'They returned home very late.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_6', 3, NULL, 'assets/topic/asb/18404.png', 'Little Goat said to his mama,  "I met a beautiful friend. I am so happy. We will meet again tomorrow."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_7', 3, NULL, 'assets/topic/asb/18405.png', 'Mama Goat answered,  "Oh my child. I am glad you are back safely. He is not our friend. Do not meet him tomorrow."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_8', 3, NULL, 'assets/topic/asb/18406.png', 'At the same time,  Little Leopard told his mother,  "I met a beautiful friend. I am happy. We will meet tomorrow."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_9', 3, NULL, 'assets/topic/asb/18407.png', 'Mama Leopard answered,  "Oh my son,  I am sorry you did not eat Little Goat. When you meet him tomorrow,  kill him and eat him."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_10', 3, NULL, 'assets/topic/asb/18408.png', 'The next day,  Little Leopard went to the Little Goat''s village.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_11', 3, NULL, 'assets/topic/asb/18409.png', 'Little Goat was not outside. He was hiding inside with his mother.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_12', 3, NULL, 'assets/topic/asb/18410.png', 'Little Leopard shouted,  "Let us go to the field to play."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_13', 3, NULL, 'assets/topic/asb/18411.png', 'Little Goat said,  "We are not friends anymore. I listen to my mama. Good children listen to their mamas."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_14', 3, NULL, 'assets/topic/asb/18412.png', 'Little Leopard was sad. He went back to his mama.', NULL);
