INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('22462_25', 0, 1, 'Till the land.');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('22462_25', 0, 2, 'Sow seeds.');
