INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582', 2, 'Kariza''s questions', 'assets/topic/asb/25965.png', 'Aloysie Uwizeyemariya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_3', 3, NULL, 'assets/topic/asb/25964.png', 'Kariza loved to ask questions. She got this habit from her parents. They used to tell her, "If you don''t ask questions when you are young, you will grow up to be an ignorant adult!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_4', 3, NULL, 'assets/topic/asb/25965.png', 'One day, Kariza asked her teacher, "Why do our parents always tell us to wash our hands before eating, even when our hands look clean?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_5', 3, NULL, 'assets/topic/asb/25966.png', 'The teacher answered, "That''s a good question, Kariza. Even when our hands look clean, they can still have germs on them."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_6', 3, NULL, 'assets/topic/asb/25967.png', 'The teacher got a microscope from the cupboard. "A microscope is a tool we use to see things that are too tiny for the human eye to see," she said.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_7', 3, NULL, 'assets/topic/asb/25968.png', 'The teacher put the slide on the microscope and this is what they saw through the viewer.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_8', 3, NULL, 'assets/topic/asb/25969.png', '"There are germs all around us, on the things we touch in our classroom, playground or at home. These germs can make us very sick," warned their teacher.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_9', 3, NULL, 'assets/topic/asb/25970.png', 'She continued, "To kill these germs, we need to wash our hands with clean water and soap, especially before eating. Also, when we are sick we must wash our hands so that we don''t spread the germs."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_10', 3, NULL, 'assets/topic/asb/25971.png', 'When she got home, Kariza found her father making an interesting tool. "What are you making?" she asked.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_11', 3, NULL, 'assets/topic/asb/25972.png', 'Kariza was surprised and said, "Oh yes! Our teacher has told us about this tool, but most of us do not know how to use it. How does it work?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_12', 3, NULL, 'assets/topic/asb/25973.png', '"First, step on this piece of wood on the floor," said father.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_13', 3, NULL, 'assets/topic/asb/25974.png', '"Then the water container will tilt and pour water onto your hands. Remember to wash with soap," he told Kariza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_14', 3, NULL, 'assets/topic/asb/25975.png', 'Kariza was happy and said, "How would I know this without asking questions? It''s true that questions lead to knowledge."', NULL);
