INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043', 2, 'The Hyena''s funeral', 'assets/topic/asb/25123.png', 'Daniel  Ewoi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_3', 3, NULL, 'assets/topic/asb/25123.png', 'One day, the hyena''s son died.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_4', 3, NULL, 'assets/topic/asb/25124.png', 'A donkey heard the news.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_5', 3, NULL, 'assets/topic/asb/25125.png', 'One old donkey said, "Brothers, we must go to the hyena''s funeral and show respect."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_6', 3, NULL, 'assets/topic/asb/25126.png', '"Listen," said the old donkey. "If we don''t go, the hyenas will be angry with us. They will have a reason to eat us."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_7', 3, NULL, 'assets/topic/asb/25127.png', 'The hyenas saw the donkeys outside their house.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_8', 3, NULL, 'assets/topic/asb/25128.png', 'This was the donkeys’ song:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_9', 3, NULL, 'assets/topic/asb/25129.png', 'The dead hyena''s uncle answered them with another song: "Your song is good. Your words are sweet. Come in, dear donkeys. Take a seat!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_10', 3, NULL, 'assets/topic/asb/25130.png', 'The donkeys were very frightened now.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_11', 3, NULL, 'assets/topic/asb/25131.png', 'The oldest donkey said, "My lords, we came to show respect to you. We are sorry that your son is dead. We will pray to God to comfort you. Now we must go back to our home."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_12', 3, NULL, 'assets/topic/asb/25132.png', 'The donkeys began to go.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_13', 3, NULL, 'assets/topic/asb/25133.png', 'The donkeys looked at each other.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_14', 3, NULL, 'assets/topic/asb/25134.png', 'The hyenas cut off the donkeys'' lips, and ate them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_15', 3, NULL, 'assets/topic/asb/25135.png', 'Again, the donkeys tried to go.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_16', 3, NULL, 'assets/topic/asb/25136.png', '"Stop!" Cried the dead hyena''s uncle. "My brothers, will you let these donkeys go? Look at them! We can see their teeth! They are laughing at us!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_17', 3, NULL, 'assets/topic/asb/25137.png', 'They jumped on the donkeys. They killed them, and began to eat them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_18', 3, NULL, 'assets/topic/asb/25138.png', 'Before the oldest donkey died, he looked at the hyenas and said, "Wicked hyenas, we came with our song to comfort you but we were wrong! Next time you are hungry, don''t pretend that you need a reason to eat your friend!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_19', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
