INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877', 2, 'Africa unity race', 'assets/topic/asb/19868.png', 'Nina Orange
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_3', 3, NULL, 'assets/topic/asb/19860.png', 'Kadogo and Juma are friends who love running. They run together every day.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_4', 3, NULL, 'assets/topic/asb/19861.png', 'One day, Juma said, "Let''s run across our continent. Let''s race to support the idea of African unity!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_5', 3, NULL, 'assets/topic/asb/19862.png', 'They decided to carry a unity torch on their journey.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_6', 3, NULL, 'assets/topic/asb/19863.png', 'From South Africa they headed along the west coast. They ran through Namibia, Angola, DRC, Congo and Cameroon. They rested in Abuja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_7', 3, NULL, 'assets/topic/asb/19864.png', 'West African runners joined the friends in Nigeria. They continued together, following the River Niger further west.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_8', 3, NULL, 'assets/topic/asb/19865.png', 'A sandstorm in Mali made it difficult to run. Juma was the strongest of all of them. He led the way safely.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_9', 3, NULL, 'assets/topic/asb/19866.png', 'At Guinea''s coast in Conakry the dusty runners washed in the sea. Then they decided to race to Morocco through Senegal and Mauritania.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_10', 3, NULL, 'assets/topic/asb/19867.png', 'At Casablanca the runners played with other young people on the beaches.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_11', 3, NULL, 'assets/topic/asb/19868.png', 'They ran through Algeria and Libya, and stopped in Egypt to visit the pyramids. Then they headed east,  following the River Nile to Uganda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_12', 3, NULL, 'assets/topic/asb/19869.png', 'They ran through deserts and national parks. More young people joined them in Kampala. The group headed for Kenya''s coast.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_13', 3, NULL, 'assets/topic/asb/19870.png', 'In Mombasa they sat on the beach and ate coconut rice with fish.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_14', 3, NULL, 'assets/topic/asb/19871.png', 'But Juma was tired. On the way to Tanzania, he fell down.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_15', 3, NULL, 'assets/topic/asb/19872.png', 'Juma passed on the torch to Kadogo. He said, "You carry the torch to Mount Kilimanjaro. Light a flame for African unity."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_16', 3, NULL, 'assets/topic/asb/19873.png', 'People cheered the runners saying, "You are our heroes."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_17', 3, NULL, 'assets/topic/asb/19874.png', 'They stopped to swim in Lake Malawi. Kadogo said to Juma, "We''ve run south to north and back again. Our journey is ending."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_18', 3, NULL, 'assets/topic/asb/19875.png', 'The unity runners finally carried the torch to Great Zimbabwe. Crowds gathered at this important place.
