INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635', 2, 'Annoying mosquito', 'assets/topic/asb/14756.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_3', 3, NULL, 'assets/topic/asb/14755.png', 'Knock! Knock!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_4', 3, NULL, 'assets/topic/asb/14756.png', '"Who is there?" I ask sleepily.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_5', 3, NULL, 'assets/topic/asb/14757.png', '"It is me," a shrill voice says.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_6', 3, NULL, 'assets/topic/asb/14758.png', '"Who is ''me''?" I ask.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_7', 3, NULL, 'assets/topic/asb/14759.png', '"I told you,  it is o-n-l-y m-e," the same voice says.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_8', 3, NULL, 'assets/topic/asb/14760.png', '"I don''t know anyone called,  ''o-n-l-y m-e''," I reply. I check that my mosquito net is tucked in around my bed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_9', 3, NULL, 'assets/topic/asb/14761.png', '"Well,  my name is Amos." The voice sounds more shrill than before. It is very close to my ear.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_10', 3, NULL, 'assets/topic/asb/14762.png', '"Amos who?" I ask as I look under the net.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_11', 3, NULL, 'assets/topic/asb/14763.png', '"Amos Quito," the voice finally says. I feel a bite on my ear.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_12', 3, NULL, 'assets/topic/asb/14764.png', '"Ouch!" I jump up and begin to search. "You will pay for this!" I shout.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_13', 3, NULL, 'assets/topic/asb/14766.png', 'Angrily,  I get out of bed and switch on the light.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_14', 3, NULL, 'assets/topic/asb/14765.png', 'I declare war on the annoying Amos Quito!', NULL);
