INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401', 2, 'Ostrich and Lioness', 'assets/topic/asb/19205.png', 'Turkana ECD Teachers
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_3', 3, NULL, 'assets/topic/asb/19195.png', 'Ostrich and Lioness were great friends. Both of them had young ones.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_4', 3, NULL, 'assets/topic/asb/19196.png', 'Ostrich fed her chicks well.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_5', 3, NULL, 'assets/topic/asb/19197.png', 'The cubs had little food to eat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_6', 3, NULL, 'assets/topic/asb/19198.png', 'Lioness looked at the chicks and thought,  "They are beautiful! I wish they were mine."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_7', 3, NULL, 'assets/topic/asb/19199.png', 'When Ostrich was away,  Lioness took the chicks to her home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_8', 3, NULL, 'assets/topic/asb/19200.png', 'Ostrich returned and found the cubs in her home. She cried,  "Where are my children?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_9', 3, NULL, 'assets/topic/asb/19201.png', 'Ostrich ran to Lioness'' hut and saw her chicks with Lioness.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_10', 3, NULL, 'assets/topic/asb/19202.png', 'Lioness refused to let Ostrich take her chicks. She said,  "They are mine now. You can have the cubs."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_11', 3, NULL, 'assets/topic/asb/19203.png', 'Ostrich was angry and upset. "What can I do to get back my children?" she wondered to herself.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_12', 3, NULL, 'assets/topic/asb/19204.png', 'Then she thought,  "I know what to do. I will call all the animals for a meeting."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_13', 3, NULL, 'assets/topic/asb/19205.png', 'The animals gathered together.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_14', 3, NULL, 'assets/topic/asb/19206.png', 'Elephant asked Ostrich and Lioness to speak.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_15', 3, NULL, 'assets/topic/asb/19207.png', 'Most of the animals were afraid to blame Lioness.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_16', 3, NULL, 'assets/topic/asb/19208.png', 'Finally,  Squirrel stood up and said,  "Children that look like birds,  belong to Ostrich. Those with tails,  belong to Lioness."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_17', 3, NULL, 'assets/topic/asb/19209.png', 'Then Squirrel disappeared into a hole. All the animals cheered.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_18', 3, NULL, 'assets/topic/asb/19210.png', 'Lioness was very annoyed. She went home with her cubs.', NULL);
