INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611', 2, 'Goat and Hyena''s knife', 'assets/topic/asb/13567.png', 'Cornelius Wekunya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_3', 3, NULL, 'assets/topic/asb/13550.png', 'Once upon a time,  there was a long dry spell. The animals ate all the grass in the country. Only the imbowa plants on the big trees survived.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_4', 3, NULL, 'assets/topic/asb/13552.png', 'One day Goat saw Hyena pass by with a knife. Goat asked for the knife so that she could cut imbowa plants to eat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_5', 3, NULL, 'assets/topic/asb/13551.png', 'Hyena gave the knife to Goat. She cut down the climbing plant,  and ate until she was full. Goat forgot all about the knife she had used.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_6', 3, NULL, 'assets/topic/asb/13566.png', 'When Hyena returned to ask for the knife,  Goat was at a loss. She could not remember where she had dropped it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_7', 3, NULL, 'assets/topic/asb/13567.png', 'Goat scratched here and there in search of the knife. Up to today Goat is still looking for that knife.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_8', 3, NULL, 'assets/topic/asb/13568.png', 'Hyena told Goat,  "Since you have lost my knife,  which was the source of my livelihood,  I will eat you. Then I will eat your offspring!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_9', 3, NULL, 'assets/topic/asb/13569.png', 'From that time,  Hyena has never spared Goat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15611_10', 3, NULL, 'assets/topic/asb/13570.png', 'Meanwhile,  Goat continues to search for Hyena''s knife.', NULL);
