INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868', 2, '(Colour-in) Goat, Dog and Cow', 'assets/topic/asb/6372.png', 'Marleen Visser', 'FFF4E1B5');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_3', 3, NULL, 'assets/topic/asb/6371.png', 'Goat, Dog and Cow were great friends. One day they went on a journey in a taxi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_4', 3, NULL, 'assets/topic/asb/6373.png', 'They reached the end of their journey. The driver asked them to pay their fares. Cow paid her fare.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_5', 3, NULL, 'assets/topic/asb/6374.png', 'Dog paid extra, because he did not have the correct money.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_6', 3, NULL, 'assets/topic/asb/6375.png', 'The driver was about to give Dog his change. Suddenly, Goat ran away without paying.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_7', 3, NULL, 'assets/topic/asb/6376.png', 'The driver was very annoyed. He drove away without giving Dog his change.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_8', 3, NULL, 'assets/topic/asb/6377.png', 'That is why, even today, Dog runs towards a car to peep inside. He is looking for the driver who owes him change.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_9', 3, NULL, 'assets/topic/asb/6378.png', 'Goat runs away from the sound of a car. She is afraid she will be arrested for not paying her fare.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_10', 3, NULL, 'assets/topic/asb/6379.png', 'Cow is not bothered by cars. Cow takes her time crossing the road. She knows she paid her fare in full.', NULL);
