INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059', 2, 'Goat, the false king', 'assets/topic/asb/13362.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_3', 3, NULL, 'assets/topic/asb/13351.png', 'Once upon a time,  there was a goat called Igodhoobe. Igodhoobe the goat was the king of farm animals and birds. He lived a good life. One day,  Igodhoobe the goat called all the animals and birds to a meeting.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_4', 3, NULL, 'assets/topic/asb/13350.png', '"My friends,  I have called you because I had a dream," said the goat. All the animals and birds kept quiet. They listened to their king carefully.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_5', 3, NULL, 'assets/topic/asb/13352.png', '"I dreamed that there was no food or water left in the land. Many of our relatives died!" said the king. When the animals and the birds heard the dream,  they were worried. "What shall we do?" asked the cat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_6', 3, NULL, 'assets/topic/asb/13353.png', 'The hen and the duck had an idea. They said,  "Let each one of us bring food and put it in the king''s store." All the animals agreed with this idea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_7', 3, NULL, 'assets/topic/asb/13362.png', 'The king made a rule. He said,  "Get ropes. Tie up anyone who does not bring food to my store. Then carry him to me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_8', 3, NULL, 'assets/topic/asb/13354.png', 'But, soon after this, it was time for a new king to rule over the farm. All the animals and birds met and elected the cat as their new king.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_9', 3, NULL, 'assets/topic/asb/13363.png', 'But the goat refused to agree that the cat was king. "I''m the king. There is no other king. I can''t obey any other animal," said the goat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_10', 3, NULL, 'assets/topic/asb/13355.png', 'The animals took food to the new king''s store. But the goat did not take anything. "When the goat was king,  we all obeyed him. Now he does not want to obey our new king. What shall we do?" asked the cow.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_11', 3, NULL, 'assets/topic/asb/13357.png', 'All the domestic animals and birds were angry. "He thinks he is different to us," they all muttered.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_12', 3, NULL, 'assets/topic/asb/13356.png', 'The dog said,  "I helped him when he was king. I did not sleep at night. I was always with him,  ready to help."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_13', 3, NULL, 'assets/topic/asb/13358.png', 'The sheep said,  "I gave him my wool to cover his children."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_14', 3, NULL, 'assets/topic/asb/13359.png', 'The pig said,  "When he was king,  he told his friends that I eat a lot. But I was often in his garden digging and weeding his maize and millet."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_15', 3, NULL, 'assets/topic/asb/13360.png', 'The sheep asked,  "What did he say? That you eat a lot? Did he think that he will be king forever?" Then the sheep added,  "Let him understand that he is not king any more. I don''t know why he thinks he is so important."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_16', 3, NULL, 'assets/topic/asb/13361.png', 'All the animals laughed and agreed that the goat must take food to the new king. King Cat ordered the goat to come to him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_17', 3, NULL, 'assets/topic/asb/13364.png', 'When the animals went to fetch the goat, he was sitting on a chair with his legs crossed. "I shall not go to the cat. There is no other king except me. When you become a king,  you are always a king," said the goat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_18', 3, NULL, 'assets/topic/asb/13365.png', '"We will tie you with ropes and take you to the new king," said the sheep.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_19', 3, NULL, 'assets/topic/asb/13366.png', 'So the cow tied the goat to her tail. She then pulled him to the king. The sheep,  the duck,  the dog,  the pig,  and the cock cheered as the cow pulled the goat. They all shouted,  "Take this stubborn goat to our new king!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_20', 3, NULL, 'assets/topic/asb/13367.png', 'From that time,  every goat refuses to move when it is pulled. It thinks that you are taking it to the king''s court.', NULL);
