INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851', 2, 'A Dog', 'assets/topic/asb/5927.png', 'Ritah Katetemera and Cissy Namugerwa
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_3', 3, NULL, 'assets/topic/asb/1393.png', 'There was a man with his one dog.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_4', 3, NULL, 'assets/topic/asb/5926.png', 'He loved his dog so much.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_5', 3, NULL, 'assets/topic/asb/3033.png', 'It was hard working and it used to go to the market to buy meat.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_6', 3, NULL, 'assets/topic/asb/2494.png', 'Fetching water from the well.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_7', 3, NULL, 'assets/topic/asb/2492.png', 'Guarding every thing at home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_8', 3, NULL, 'assets/topic/asb/1757.png', 'One day the man thanked his dog in a special way for being faithful.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23851_9', 3, NULL, 'assets/topic/asb/2490.png', 'The dog became so happy and it continued working hard.', NULL);
