INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865', 2, 'Hungry Jackal and the sour grapes', 'assets/topic/asb/1775.png', 'Kholeka Mabeta
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_3', 3, NULL, 'assets/topic/asb/1776.png', 'Once upon a time, there was a hungry jackal. He was walking around the bush looking for food.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_4', 3, NULL, 'assets/topic/asb/1775.png', 'He came across a grapevine. He spotted a bunch of juicy ripe grapes hanging from a high branch.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_5', 3, NULL, 'assets/topic/asb/1777.png', '"Those look really delicious," he thought. He took a few steps back so that he could make his highest jump.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_6', 3, NULL, 'assets/topic/asb/1778.png', 'The grapes were too high. Jackal fell on his back without even touching the bunch.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_7', 3, NULL, 'assets/topic/asb/1779.png', 'Jackal got up and went further back from his starting spot. He ran, and almost got high enough. But he fell even harder.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_8', 3, NULL, 'assets/topic/asb/1780.png', 'He tried again and again. But he couldn''t get to the grapes. He kept falling until his back was very sore.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_9', 3, NULL, 'assets/topic/asb/1781.png', 'Finally, he gave up and walked away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_10', 3, NULL, 'assets/topic/asb/1782.png', 'He stopped and looked back at the grapes. He was hungrier than ever. "Those grapes look really sour anyway," he thought.', NULL);
