INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085', 2, 'Teret', 'assets/topic/asb/19575.png', 'Beza', 'FFCFB5DD');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_3', 3, NULL, 'assets/topic/asb/19569.png', 'There was a shepherd who had no grass to feed his pig, sheep and goat and no grains for his hen. He had no barn to keep them warm.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_4', 3, NULL, 'assets/topic/asb/19572.png', 'He decided to take them where they could get food and shelter.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_5', 3, NULL, 'assets/topic/asb/19573.png', 'The shepherd said, "I could sell them for a quarter each." But the man was negotiating for a lower price.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_6', 3, NULL, 'assets/topic/asb/19574.png', 'The man said, "I only want the goat." But the shepherd said, "These animals are friends. I want all of them to be together."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_7', 3, NULL, 'assets/topic/asb/19575.png', 'The shepherd sold the sheep, the goat and the pig for a quarter each. He gave the hen for free. He was sad as he headed home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_8', 3, NULL, 'assets/topic/asb/19577.png', 'He realised they weren''t just animals. They were also his friends. So he went back to return the money and take his friends home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_9', 3, NULL, 'assets/topic/asb/19578.png', '"They are mine now," the man said. "I won''t give them back." But the shepherd kept begging.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_10', 3, NULL, 'assets/topic/asb/19581.png', '"I realised they are not just animals but also my friends," said the shepherd. The man asked, "Why did you sell them in the first place?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_11', 3, NULL, 'assets/topic/asb/19582.png', 'A neighbour heard the argument and said, "Make a barn, provide grass and grains. Then you can get your animals back."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_12', 3, NULL, 'assets/topic/asb/19572.png', 'The shepherd loved his animals. He gave back the money and took home his friends to a perfect barn and plenty of grass and grains.', NULL);
