INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469', 2, 'Cassava and Palm', 'assets/topic/asb/25923.png', 'Divine  Apedo
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_3', 3, NULL, 'assets/topic/asb/25919.png', 'Long time ago, Cassava and Palm lived in a village called Koowa. They were very good friends. They visited each other every day. They were farmers. They worked very hard on their farms.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_4', 3, NULL, 'assets/topic/asb/25920.png', 'In a certain year, there was no rain. The crops did not grow well. All the plants dried up. People did not have any food to eat.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_5', 3, NULL, 'assets/topic/asb/25921.png', 'On their way, they met a woman. "Good afternoon," they greeted her.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_6', 3, NULL, 'assets/topic/asb/25922.png', '"What work can you do?" the woman asked. The friends responded, "We can provide food for your family and animals."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_7', 3, NULL, 'assets/topic/asb/25923.png', 'The woman took them home with her.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_8', 3, NULL, 'assets/topic/asb/25924.png', 'One afternoon, Palm tree and Cassava had an argument. Cassava said he was more important than Palm.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_9', 3, NULL, 'assets/topic/asb/25925.png', 'The woman heard them from her room. She came out and asked "Why are you fighting?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_10', 3, NULL, 'assets/topic/asb/25926.png', 'Cassava spoke first. "I am more important than Palm tree. I provide tubers for your fufu, gari, boiled cassava and cassava dough."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_11', 3, NULL, 'assets/topic/asb/25927.png', '"My stems are used for planting. My leaves and peels feed your animals. What does Palm tree do?" asked Cassava.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_12', 3, NULL, 'assets/topic/asb/25928.png', 'Palm tree laughed, shook his head and said, "Woman, do you remember the palm soup you enjoy so much? I provide it."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_13', 3, NULL, 'assets/topic/asb/25929.png', '"I provide the palm oil for preparing kpokpoi and the palm kernel oil for frying fish and meat. How would you prepare stews and gravies without oil?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_14', 3, NULL, 'assets/topic/asb/25930.png', '"In addition, my fronds provide shade for your huts. You cool your heart with my sweet, rich, foamy palm wine after your hard day''s work."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_15', 3, NULL, 'assets/topic/asb/25931.png', '"Above all, the brooms that are used to sweep the rooms and compounds come from me," concluded Palm.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_16', 3, NULL, 'assets/topic/asb/25932.png', '"Hmmm!" sighed the woman. "Alright friends, I have heard you. I will settle this problem."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_17', 3, NULL, 'assets/topic/asb/25933.png', '"Both of you are very important to me. The two of you together make a delicious meal, fufu and palm-nut soup!" said the woman.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22469_18', 3, NULL, 'assets/topic/asb/25934.png', 'The woman prepared palm nut soup with fufu from cassavas. She invited her friends to eat with her. They enjoyed the meal very much.
