INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871', 2, 'My family and I', 'assets/topic/asb/5216.png', 'Cornelius Wambi Gulere
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_3', 3, NULL, 'assets/topic/asb/5217.png', 'My dinosaur and I look at my birds in the sky.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_4', 3, NULL, 'assets/topic/asb/5218.png', 'My birds and I meet Chimpanzee in the tree.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_5', 3, NULL, 'assets/topic/asb/5219.png', 'My chimpanzee and I play with my giraffes on the hill.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_6', 3, NULL, 'assets/topic/asb/5232.png', 'My giraffes and I make monkey balls from clay.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_7', 3, NULL, 'assets/topic/asb/5221.png', 'My monkey and I eat bananas with Gorilla every day.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_8', 3, NULL, 'assets/topic/asb/5220.png', 'My gorilla and monkey fear Tortoise in every way.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_9', 3, NULL, 'assets/topic/asb/5222.png', 'Tortoise and I go home very slowly with Snake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_10', 3, NULL, 'assets/topic/asb/5223.png', 'Snake and I love to hiss and sing songs by the lake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7871_11', 3, NULL, 'assets/topic/asb/5224.png', 'My entire family and I live together in great happiness!', NULL);
