INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082', 2, 'Fire''s story', 'assets/topic/asb/8558.png', 'African Storybook and Lisa Treffry-Goatley
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_3', 3, NULL, 'assets/topic/asb/8555.png', 'Let me tell you my story. I start as a small spark.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_4', 3, NULL, 'assets/topic/asb/8556.png', 'I run wild when the wind carries me.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_5', 3, NULL, 'assets/topic/asb/8558.png', 'We are good friends,  you and I.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_6', 3, NULL, 'assets/topic/asb/8557.png', 'I cook your food.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_7', 3, NULL, 'assets/topic/asb/8559.png', 'I light your night.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_8', 3, NULL, 'assets/topic/asb/8560.png', 'I have many tricks.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_9', 3, NULL, 'assets/topic/asb/8561.png', 'I am part of your family.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22082_10', 3, NULL, 'assets/topic/asb/8562.png', 'But be careful when you invite me to your home. I don''t like to be left alone.', NULL);
