INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_4', 3, NULL, 'assets/topic/asb/1494.png', 'Ine ndili ndekha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_5', 3, NULL, 'assets/topic/asb/1495.png', 'Ndimakondanso masewero a nthambo ndi anzanga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_6', 3, NULL, 'assets/topic/asb/1496.png', 'Ndimakonda kuwerenga nthano zabwino.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_7', 3, NULL, 'assets/topic/asb/1497.png', 'Ndimakonda kuyenda mu dambo ndi anzanga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_8', 3, NULL, 'assets/topic/asb/1498.png', 'Ndimakonda kutsuka mbale za amai.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_9', 3, NULL, 'assets/topic/asb/1499.png', 'Ndimakonda kulemba malembo akulu akulu amene sabvuta kuwerenga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_10', 3, NULL, 'assets/topic/asb/1500.png', 'Ndimakonda kudya zipatso zosiyana siyana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2106_11', 3, NULL, 'assets/topic/asb/1501.png', 'Ndimakonda kugona pa bedi lalikulu.
