INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301', 2, 'I enjoy doing', 'assets/topic/asb/1496.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_3', 3, NULL, 'assets/topic/asb/1495.png', 'I enjoy skipping with my friend.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_4', 3, NULL, 'assets/topic/asb/1499.png', 'I enjoy writing letters on the board.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_5', 3, NULL, 'assets/topic/asb/1496.png', 'I enjoy reading picture books.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_6', 3, NULL, 'assets/topic/asb/1500.png', 'I enjoy eating fruit.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_7', 3, NULL, 'assets/topic/asb/1498.png', 'I enjoy helping at home.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_8', 3, NULL, 'assets/topic/asb/1492.png', 'I enjoy listening to my mother''s stories.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_9', 3, NULL, 'assets/topic/asb/1501.png', 'I enjoy sleeping on a big bed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31301_10', 3, NULL, 'assets/topic/asb/1502.png', 'I enjoy doing these things.
