INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159', 2, 'Nozibele and the three hairs', 'assets/topic/asb/1196.png', 'Tessa Welch
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_3', 3, NULL, 'assets/topic/asb/1197.png', 'A long time ago,  three girls went out to collect wood.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_4', 3, NULL, 'assets/topic/asb/1198.png', 'It was a hot day so they went down to the river to swim. They played and splashed and swam in the water.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_5', 3, NULL, 'assets/topic/asb/1196.png', 'Suddenly,  they realised that it was late. They hurried back to the village.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_6', 3, NULL, 'assets/topic/asb/1199.png', 'When they were nearly home,  Nozibele put her hand to her neck. She had forgotten her necklace! "Please come back with me!" she begged her friends. But her friends said it was too late.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_7', 3, NULL, 'assets/topic/asb/1200.png', 'Nozibele went back to the river alone. She found her necklace and began hurrying home. But,  she got lost in the dark.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_8', 3, NULL, 'assets/topic/asb/1201.png', 'In the distance she saw light coming from a hut. She walked quickly towards it and knocked at the door.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_9', 3, NULL, 'assets/topic/asb/1202.png', 'To her surprise,  a dog opened the door and said,  "What do you want?" "I''m lost and I need a place to sleep," said Nozibele. "Come in,  or I''ll bite you!" said the dog. Nozibele went in.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_10', 3, NULL, 'assets/topic/asb/1203.png', 'The dog said,  "Cook for me!" "But I''ve never cooked for a dog before," Nozibele answered. "Cook,  or I''ll bite you!" said the dog. Nozibele cooked some food for the dog.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_11', 3, NULL, 'assets/topic/asb/1204.png', 'Then the dog said,  "Make the bed for me!" Nozibele answered,  "I''ve never made a bed for a dog." "Make the bed,  or I''ll bite you!" the dog said. Nozibele made the bed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_12', 3, NULL, 'assets/topic/asb/1207.png', 'Every day she had to cook and sweep and wash for the dog. Then one day the dog said,  "Nozibele,  today I have to visit some friends. Sweep the house,  cook the food and wash my things before I come back."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_13', 3, NULL, 'assets/topic/asb/1208.png', 'As soon as the dog had gone,  Nozibele took three hairs from her head. She put one hair under the bed,  one behind the door,  and one in the kraal. Then she ran home as fast as she could.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_14', 3, NULL, 'assets/topic/asb/1209.png', 'When the dog came back,  he looked for Nozibele. "Nozibele,  where are you?" he shouted. "I''m here,  under the bed," said the first hair. "I''m here,  behind the door," said the second hair. "I''m here,  in the kraal," said the third hair.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_15', 3, NULL, 'assets/topic/asb/1210.png', 'The dog knew that Nozibele had tricked him. He ran and ran all the way to the village. But Nozibele''s brothers were waiting there with big sticks. The dog turned and ran away and has never been seen since.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3159_16', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994', 2, 'Baboon chief', 'assets/topic/asb/18900.png', 'Bonsamo Miesso
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_3', 3, NULL, 'assets/topic/asb/18894.png', 'There was a huge baboon with a large troop of baboons that followed him. He was a real dictator!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_4', 3, NULL, 'assets/topic/asb/18895.png', 'But there were many huge baboons in the troop,  and the herd was often not sure which one was giving the orders.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_5', 3, NULL, 'assets/topic/asb/18896.png', 'So the other baboons said to the chief,  "You need a special mark,  so that we can be sure who you are. Then we''ll be able to follow your orders."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_6', 3, NULL, 'assets/topic/asb/18897.png', '"All right," said the chief. "I''ll wear a band round my head. But if I do that you must make sure that you follow my instructions exactly." They all agreed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_7', 3, NULL, 'assets/topic/asb/18898.png', 'So a big baboon tied a head band round the chief''s head. The chief said,  "From today,  whatever I tell you to do you must do. I don''t want any disobedience."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_8', 3, NULL, 'assets/topic/asb/18899.png', 'When the chief jumped the others jumped,  when he sat down the others sat down. When he screamed,  the others would scream.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_9', 3, NULL, 'assets/topic/asb/18900.png', 'As time went by the head band became tighter and tighter.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_10', 3, NULL, 'assets/topic/asb/18901.png', 'One day the chief sat down and said,  "This head band is hurting me." And he put his head in his hands.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_11', 3, NULL, 'assets/topic/asb/18902.png', 'And all the baboons sat down,  and they put their heads in their hands,  and they said,  "This head band is hurting me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_12', 3, NULL, 'assets/topic/asb/18903.png', 'He said,  "No,  I''m serious." And they all said,  "No,  I''m serious." The chief said,  "This thing''s killing me." And they all said,  "This thing''s killing me."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_13', 3, NULL, 'assets/topic/asb/18904.png', 'And then he fell off the rock. And they all imitated him and fell off the rock. Then he began struggling and they began struggling.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_14', 3, NULL, 'assets/topic/asb/18905.png', 'Although the rest of the troop survived,  the chief died from his injuries.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21994_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987', 2, 'The baboons that went this way and that', 'assets/topic/asb/1160.png', 'Southern African Folktale
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_3', 3, NULL, 'assets/topic/asb/1161.png', 'The people in a village of small huts were becoming unhappy. Wild animals began to come and frighten them.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_4', 3, NULL, 'assets/topic/asb/1162.png', 'These animals ate all their crops and even carried children away. It was not a good place to live any longer,  but where could the people go?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_5', 3, NULL, 'assets/topic/asb/1163.png', 'One family found the answer. It was not hard to find food up in the hills.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_6', 3, NULL, 'assets/topic/asb/1164.png', 'Bushes grew in the cracks between rocks. Trees grew on the slopes of the hills. The parents trapped rock rabbits and the children caught birds.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_7', 3, NULL, 'assets/topic/asb/1165.png', 'Other families noticed how well the hill family lived. "It is a good life up in the hills," said the husband. "You should come there too."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_8', 3, NULL, 'assets/topic/asb/1166.png', 'Soon the other families left the flat land and went up to the hills. Each family found a cave to live in,  and felt warm and secure.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_9', 3, NULL, 'assets/topic/asb/1160.png', 'Soon the children became very quick at climbing rocks,  and finding fruit in the trees. They could swing in the branches almost as well as any monkey.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_10', 3, NULL, 'assets/topic/asb/1167.png', 'Slowly,  things began to change. The children talked less,  and grunted more. Then the adults noticed that their noses were getting bigger and that they were growing more hair.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_11', 3, NULL, 'assets/topic/asb/1169.png', 'Every time they looked at one another they saw that their teeth were longer. They started to walk on four legs.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_12', 3, NULL, 'assets/topic/asb/1175.png', 'They became a new creature that no-one had seen before in that place. This was the creature which people now call the baboon.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_13', 3, NULL, 'assets/topic/asb/1168.png', 'For a time,  the baboons lived happily. They stopped chasing the rock rabbits and ate grubs from the ground.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_14', 3, NULL, NULL, 'They forgot how to talk. They took off their clothes because their legs and arms were covered with dark hair.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_15', 3, NULL, 'assets/topic/asb/1170.png', 'They still remembered that they had been people. They looked at each other''s big noses and laughed at each other.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_16', 3, NULL, 'assets/topic/asb/1171.png', 'But they did not like the laughing. They jumped up and down in anger. Soon the baboons could not be together because of the laughing and the anger. Each family went off and lived by itself.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_17', 3, NULL, 'assets/topic/asb/1173.png', 'That is why baboons live in small groups today and do not live as a baboon nation.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_18', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692', 2, 'Tortoise finds his house', 'assets/topic/asb/9213.png', 'Maya Fowler
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_3', 3, NULL, 'assets/topic/asb/9214.png', 'One day Tortoise was walking through the veld. He was looking for something. He gazed into the distance and squinted at the grass. Then he walked past Snail.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_4', 3, NULL, NULL, '"Are you looking for something?" Snail asked. "Yes,  Snail,  I''m looking for my house. Have you seen it,  by any chance?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_5', 3, NULL, 'assets/topic/asb/9215.png', 'Snail said,  "No,  I haven''t,  but I''ll help you look!" She climbed onto Tortoise''s shell. Tortoise walked on with Snail on his back. They looked and looked,  but there was no sign of a house. The sun was high in the sky.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_6', 3, NULL, 'assets/topic/asb/9216.png', 'A little later they passed Sparrow. "Are you looking for something?" asked Sparrow. "Yes,  Sparrow,  I''m looking for my house. Have you seen it,  by any chance?" Sparrow said,  "No,  I haven''t,  but I''ll help you look!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_7', 3, NULL, 'assets/topic/asb/9213.png', 'He flapped his wings and he whizzed away. "But you''re going too fast for me," complained Tortoise. Sparrow came back. "Alright,  then I''ll hop on too."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_8', 3, NULL, 'assets/topic/asb/9223.png', 'Tortoise carried on,  with Snail and Sparrow perched on his shell. They looked and looked,  but there was no sign of a house. The sun had inched lower in the sky.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_9', 3, NULL, 'assets/topic/asb/9218.png', 'A little later they passed Ladybird. "Are you looking for something?" asked Ladybird. "Yes,  Ladybird,  I''m looking for my house. Have you seen it,  by any chance?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_10', 3, NULL, 'assets/topic/asb/9219.png', 'Ladybird said,  "No,  I haven''t,  but I''ll help you look!" She hopped onto Tortoise''s back and on he went,  with Snail and Sparrow and Ladybird on his back. They looked and looked,  but there was no sign of a house. A breeze started to blow.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_11', 3, NULL, 'assets/topic/asb/9220.png', 'A little later they passed Mouse. He was making a garland of daisies. "Are you looking for something?" asked Mouse. "Yes,  Mouse,  I''m looking for my house. Have you seen it,  by any chance?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_12', 3, NULL, 'assets/topic/asb/9221.png', 'Mouse said: "No,  I haven''t,  but I''ll help you look!" She hopped onto Tortoise''s back. "Wait,  I can''t leave my flowers behind." Tortoise waited. Mouse stretched and stretched and scooped up her daisies.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_13', 3, NULL, 'assets/topic/asb/9222.png', 'Tortoise walked on,  with Snail and Sparrow and Ladybird and Mouse on his back. They looked and looked,  but there was no sign of a house. The wind swept leaves off the ground and swirled them around Tortoise''s feet. He was tired. "You lot are heavy," he sighed.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_14', 3, NULL, 'assets/topic/asb/9224.png', 'The wind got stronger. The sky turned dark. "Oh,  where is my house?" asked Tortoise. Thunder roared in the hills. Lightning flashed in the distance. Drip-drip came the rain.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_15', 3, NULL, 'assets/topic/asb/9227.png', 'The wind whirled and whipped. It blew Snail and Sparrow and Ladybird and Mouse right off Tortoise''s back.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_16', 3, NULL, 'assets/topic/asb/9226.png', 'And then came the hail,  tip-tip-tip. Tortoise got such a fright he shrank into his shell. It was warm and cosy in there. "Oh,  here is my house!" smiled Tortoise.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_17', 3, NULL, 'assets/topic/asb/9225.png', '"Oh,  here is Tortoise''s house!" said Snail and Robin and Mouse.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_18', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073', 2, 'Wardit, the mule', 'assets/topic/asb/17977.png', 'Mesfin Habte-Mariam
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_3', 3, NULL, 'assets/topic/asb/17968.png', 'Once upon a time,  there was a young mule called Wardit. She was clever,  and beautiful. She was also very proud. One day,  Wardit was going to the river to drink some water.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_4', 3, NULL, 'assets/topic/asb/17969.png', 'A handsome young horse was walking along the same road. The horse saw Wardit and thought,  "Here is the most beautiful mule in the world! I must marry her."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_5', 3, NULL, 'assets/topic/asb/17970.png', '"Where are you going?" the horse asked Wardit. "I''m going to the river to drink water," she replied. "Let me come with you," the horse said.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_6', 3, NULL, 'assets/topic/asb/17971.png', 'The horse said,  "Wardit,  you are the most beautiful mule in the world! Please,  be my wife!" Wardit looked at the horse. She thought,  "He is young,  handsome,  and strong. Yes,  I will marry him!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_7', 3, NULL, 'assets/topic/asb/17972.png', 'The horse wanted to know everything about his future wife. He asked,  "Who is your father? Who is your mother?" Wardit turned her head away. She didn''t answer him. The horse said,  "My dear,  I must know the family of my bride."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_8', 3, NULL, 'assets/topic/asb/17973.png', 'Wardit laughed. She said,  "You can see my mother every day. She lives in the palace. The governor rides her each morning." "But who is your father?" the horse asked.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_9', 3, NULL, 'assets/topic/asb/17974.png', 'Wardit said,  "My sister belongs to the priest. Don''t you know her? He rides her to the church." "Yes,  but who is your father? Where is he?" the horse asked again.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_10', 3, NULL, 'assets/topic/asb/17975.png', 'Wardit went on,  "And my aunt is so beautiful! She lives with the headman of the village. He loves her like his daughter." "That''s very good,  but please,  tell me about your father," the horse said.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_11', 3, NULL, 'assets/topic/asb/17976.png', 'Just then,  an old donkey came down the road. It was Wardit''s father. She looked at him,  and she was ashamed. "My father is only a donkey. The horse will not want a donkey to be his father-in-law," thought Wardit.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_12', 3, NULL, 'assets/topic/asb/17977.png', 'The donkey was near Wardit now. She did not greet him. She did not even look at him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_13', 3, NULL, 'assets/topic/asb/17978.png', '"Wardit!" exclaimed the old donkey. "What''s the matter,  my girl?" he asked. "Why don''t you speak to me?" The donkey became angry with his daughter. Wardit said nothing. She looked away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_14', 3, NULL, 'assets/topic/asb/17979.png', 'Then the horse became angry. He said to Wardit,  "Who is this foolish old donkey? Why is he speaking to you?" "I don''t know him," said Wardit.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_15', 3, NULL, 'assets/topic/asb/17980.png', '"You don''t know me? Wardit,  I am your" cried the old donkey. But the horse wasn''t listening.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_16', 3, NULL, 'assets/topic/asb/17981.png', '"Go away!" shouted the horse. "You foolish old donkey. Leave us alone!" The old donkey didn''t move. The horse kicked him. The donkey fell down.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_17', 3, NULL, 'assets/topic/asb/17982.png', 'The donkey looked up to the sky. "Oh God," he cried. "Did you see this?" The poor old donkey''s heart broke,  and he died.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_18', 3, NULL, 'assets/topic/asb/17984.png', 'God heard the donkey''s words. God said to Wardit,  "Your father is dead,  and now I will punish you. Horses will have children,  and donkeys will have children. But you,  the mule,  will never have a child. This is your punishment because you did not respect your father."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_19', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270', 2, 'Face', 'assets/topic/asb/26803.png', 'Little Zebra Books
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_3', 3, NULL, 'assets/topic/asb/26807.png', 'Eyes', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_4', 3, NULL, 'assets/topic/asb/26808.png', 'Ears', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_5', 3, NULL, 'assets/topic/asb/26802.png', 'Mouth', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_6', 3, NULL, 'assets/topic/asb/26800.png', 'Nose', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_7', 3, NULL, 'assets/topic/asb/26801.png', 'Chin', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_8', 3, NULL, 'assets/topic/asb/26755.png', 'Teeth', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_9', 3, NULL, 'assets/topic/asb/26810.png', 'Tongue', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('30270_10', 3, NULL, 'assets/topic/asb/26809.png', 'Cheeks', NULL);
