INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_12', 3, NULL, 'assets/topic/asb/8526.png', 'It was not too long before the thieves were back. Their boss was the first to go into the house. He had just opened the door when the old rooster pecked him with its beak. Then the cat scratched him with its nails. And then he found the dog,  which bit him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_13', 3, NULL, 'assets/topic/asb/8527.png', 'The man jumped but continued into the house. He found the horse,  which kicked him to death. The other thieves did not enter the house. They ran away,  never to return.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_14', 3, NULL, 'assets/topic/asb/8528.png', 'The animals lived happily ever after in that house. The old rooster told the time to his friends,  and the dog kept them safe. The cat killed all the rats in the house,  and the horse carried them on its back wherever they wanted to go.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758', 2, 'Where is my bat?', 'assets/topic/asb/1556.png', 'Madhav Chavan
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_3', 3, NULL, 'assets/topic/asb/1557.png', 'I have lost my bat,  and I cannot find it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_4', 3, NULL, 'assets/topic/asb/1558.png', 'I looked behind the door. It was not there.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_5', 3, NULL, 'assets/topic/asb/1559.png', 'I looked for it on the shelf. I could not find it there.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_6', 3, NULL, 'assets/topic/asb/1560.png', 'I looked for it under the bed. Not there either.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_7', 3, NULL, 'assets/topic/asb/1561.png', 'I searched inside the box. There was no sign of it.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_8', 3, NULL, 'assets/topic/asb/1562.png', 'I searched the attic. It was not there.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_9', 3, NULL, 'assets/topic/asb/1563.png', 'I looked here,  there and everywhere. I could not find it anywhere.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_10', 3, NULL, 'assets/topic/asb/1564.png', NULL, NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2758_11', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970', 2, 'Where is my cat?', 'assets/topic/asb/1565.png', 'Clare Verbeek
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_3', 3, NULL, 'assets/topic/asb/1565.png', 'Where is my cat?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_4', 3, NULL, 'assets/topic/asb/1822.png', 'Is it under the bed?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_5', 3, NULL, 'assets/topic/asb/1566.png', 'Is it on top of the cupboard?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_6', 3, NULL, 'assets/topic/asb/1567.png', 'Is it behind the couch?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_7', 3, NULL, 'assets/topic/asb/1568.png', 'Is it next to the bin?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_8', 3, NULL, 'assets/topic/asb/1569.png', 'Is it inside the basket?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_9', 3, NULL, 'assets/topic/asb/1570.png', 'Is it outside the house?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1970_10', 3, NULL, 'assets/topic/asb/1823.png', 'Here it is!', NULL);
