INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479', 2, 'Look at the animals', 'assets/topic/asb/1747.png', 'Jenny Katz
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_3', 3, NULL, 'assets/topic/asb/1749.png', 'Look at the animals.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_4', 3, NULL, 'assets/topic/asb/1750.png', 'The cow says, "Moo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_5', 3, NULL, 'assets/topic/asb/1751.png', 'The goat says, "Meh,  meh."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_6', 3, NULL, 'assets/topic/asb/1752.png', 'The horse says, "Neigh."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_7', 3, NULL, 'assets/topic/asb/1753.png', 'The pig says, "Grunt."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_8', 3, NULL, 'assets/topic/asb/1754.png', 'The chicken says, "Cluck."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_9', 3, NULL, 'assets/topic/asb/1755.png', 'The dog says, "Woof."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_10', 3, NULL, 'assets/topic/asb/1757.png', 'The farmer says, "Shhh!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9479_11', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505', 2, 'Children of wax', 'assets/topic/asb/1295.png', 'Southern African Folktale
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_3', 3, NULL, 'assets/topic/asb/1296.png', 'Once upon a time, there lived a happy family.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_4', 3, NULL, 'assets/topic/asb/1297.png', 'The children never fought with each other. They helped their parents at home and in the fields.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_5', 3, NULL, 'assets/topic/asb/1298.png', 'But they were not allowed to go near a fire.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_6', 3, NULL, 'assets/topic/asb/1299.png', 'They had to do all their work during the night.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_7', 3, NULL, NULL, 'Because they were made of wax!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_8', 3, NULL, 'assets/topic/asb/1300.png', 'But one of the boys longed to go out in the sunlight.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_9', 3, NULL, 'assets/topic/asb/1301.png', 'One day the longing was too strong. His brother and sister warned him.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_10', 3, NULL, 'assets/topic/asb/1302.png', 'But it was too late! He melted in the hot sun.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_11', 3, NULL, 'assets/topic/asb/1303.png', 'The wax children cried to see their brother melting away.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_12', 3, NULL, 'assets/topic/asb/1304.png', 'But they made a plan. They shaped the lump of melted wax into a bird.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_13', 3, NULL, 'assets/topic/asb/1305.png', 'They took their bird brother up to a high mountain.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_14', 3, NULL, 'assets/topic/asb/1306.png', 'And as the sun rose, he flew away, singing in the morning light.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746', 2, 'People who forgot', 'assets/topic/asb/27234.png', 'Edwin  Irabor
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_3', 3, NULL, 'assets/topic/asb/27230.png', 'Once upon a time, there was a village called Izagodo. All the people in this village were very young.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_4', 3, NULL, 'assets/topic/asb/27231.png', 'The young men and women of Izagodo danced and had fun. They ate what they liked and drank anything that made them drunk. They had fun all day and all night.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_5', 3, NULL, 'assets/topic/asb/27232.png', 'They also forgot anything that happened the day before.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_6', 3, NULL, 'assets/topic/asb/27233.png', 'There was no music for dancing. But they couldn''t even dance anymore because they were hungry and weak.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_7', 3, NULL, 'assets/topic/asb/27234.png', 'They cried and wailed.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_8', 3, NULL, 'assets/topic/asb/27235.png', 'The people shouted, "You are our chief! You must save us!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_9', 3, NULL, 'assets/topic/asb/27236.png', 'So Oza went into his hut, packed his things, and left.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_10', 3, NULL, 'assets/topic/asb/27237.png', 'Then a small but clear voice said, "I will be your chief!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_11', 3, NULL, 'assets/topic/asb/27238.png', 'To the surprise of everyone, a little boy stepped out of the crowd.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_12', 3, NULL, 'assets/topic/asb/27239.png', 'The young people were moved by the child''s courage and promised to obey him.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_13', 3, NULL, 'assets/topic/asb/27240.png', 'With the little boy as their chief, the people of Izagodo began a life of hard work. They worked and worked.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_14', 3, NULL, 'assets/topic/asb/27241.png', 'Many nights the little chief would quietly leave the village, to meet with a wise old man.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814', 2, 'Baby who cried', 'assets/topic/asb/10483.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_3', 3, NULL, 'assets/topic/asb/10477.png', NULL, NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_4', 3, NULL, 'assets/topic/asb/10478.png', NULL, NULL);
