INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24752_6', 3, NULL, 'assets/topic/asb/27155.png', 'Hata hivyo, Abby alisema, "Nina uwezo wa kufanya kazi yoyote inayofanywa na watu wengine." Alifaulu kuwashawishi.
