INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24752_9', 3, NULL, 'assets/topic/asb/27148.png', 'Siku moja alipata wazo zuri.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24752_10', 3, NULL, 'assets/topic/asb/27150.png', 'Tangazo lilisema, "Ninawasafirisha bila malipo, wanawake waja wazito, akina mama waliojifungua, na watoto."
