INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_3', 3, NULL, 'assets/topic/asb/8025.png', 'Siku moja mamangu alinunua matunda aina tofauti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_4', 3, NULL, 'assets/topic/asb/8032.png', 'Tulijiuliza, "Mama atatugawia matunda wakati gani?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_5', 3, NULL, 'assets/topic/asb/8029.png', 'Baadaye, kakangu, Rahim, alijificha na kuyala matunda yote!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_6', 3, NULL, 'assets/topic/asb/8027.png', 'Tulisema kwa hasira, "Rahim ni mtukutu tena ni mchoyo. Lazima aadhibiwe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_7', 3, NULL, 'assets/topic/asb/8023.png', 'Mama aligundua kuwa matunda yote yalikuwa yameliwa. Alikasirika sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_8', 3, NULL, 'assets/topic/asb/8024.png', 'Tulimtazama Rahim kwa ukali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_9', 3, NULL, 'assets/topic/asb/8030.png', 'Nilimwuliza mama, "Je, utamwadhibu Rahim?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_10', 3, NULL, 'assets/topic/asb/8033.png', 'Baadaye, Rahim alipata adhabu asiyoitarajia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_11', 3, NULL, 'assets/topic/asb/8031.png', 'Hakuweza kutulia!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_12', 3, NULL, 'assets/topic/asb/8034.png', 'Mama alipomwuliza alijibu kwa sauti ya chini,  "Naumwa na tumbo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8074_13', 3, NULL, 'assets/topic/asb/8035.png', 'Hiyo ilikuwa adhabu mbaya kuliko kama Rahim angechapwa na mama.', NULL);
