INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403', 2, 'Adie na Adhoch', 'assets/topic/asb/14705.png', 'Christine Nyangate
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_3', 3, NULL, 'assets/topic/asb/9066.png', 'Hapo zamani za kale, palikuwa na mwanamme mmoja aliyekuwa na wake wawili. Wake zake walipata watoto wasichana wawili, Adie na Adhoch. Watoto hao walipendana sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_4', 3, NULL, 'assets/topic/asb/13839.png', 'Adie alikuwa mwenye bidii, mkarimu na mwenye nidhamu. Adhoch naye alikuwa mvivu, mchoyo na mtovu wa nidhamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_5', 3, NULL, 'assets/topic/asb/13843.png', 'Adie alipendwa na walimu. Alipata zawadi nyingi kutokana na bidii yake. Adhoch hakupata zawadi yoyote. Mamake Adhoch alimwonea wivu Adie.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_6', 3, NULL, 'assets/topic/asb/13837.png', 'Mamake Adie aliugua na kufa. Adie hakupendwa na mamake wa kambo kama alivyopendwa na mamake mzazi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_7', 3, NULL, 'assets/topic/asb/13838.png', 'Adie alizifanya kazi zote za nyumbani. Hakupata nafasi ya kujitayarisha kwenda shule. Adhoch alisoma na kucheza na wenzake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_8', 3, NULL, 'assets/topic/asb/8023.png', 'Hata hivyo Adie alifanya vyema katika masomo yake shuleni. Alipita mitihani yake na kuendelea kupata zawadi. Hili lilimkasirisha mamake Adhoch zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_9', 3, NULL, 'assets/topic/asb/15112.png', 'Siku moja mamake Adhoch alimtaka Adie aende naye kuchota maji ziwani. Walipofika, mamake Adhoch alimwambia achote maji mbali na ukingo wa maji safi. Mawimbi yalimbeba Adie akazama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_10', 3, NULL, 'assets/topic/asb/13697.png', 'Mama alirudi nyumbani. Adhoch alimwuliza, "Mama,  Adie yuko wapi?" Mamake Adhoch alisema, "Anacheza na wenzake. Atakuja nyumbani baadaye."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_11', 3, NULL, 'assets/topic/asb/1099.png', 'Jioni, Adhoch alienda kumtafuta dada yake. Alimwita,  "Adie! Uko wapi?" Adhoch akasikia sauti ikisema, "Niko ziwani. Mama alinizamisha."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_12', 3, NULL, 'assets/topic/asb/1195.png', 'Adie aliingia majini akimwita dada yake. "Adie uko wapi?" Alisikia jibu kama lile la kwanza, "Niko majini,  mama alinizamisha."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_13', 3, NULL, 'assets/topic/asb/15112.png', 'Adhoch aliingia majini naye akazama kama vile dada yake alivyozama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15403_14', 3, NULL, 'assets/topic/asb/15011.png', 'Mamake Adhoch alikuwa na huzuni sana. Alimpoteza Adhoch kwa sababu ya wivu. Alifanya kazi zote peke yake.', NULL);
