INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218', 2, 'Adun, mrembo', 'assets/topic/asb/13337.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_3', 3, NULL, 'assets/topic/asb/13337.png', 'Adun alikuwa msichana mrembo sana. Wanaume kutoka kijijini kwake, wangependa wamuoe. Lakini, Adun aliwakataa wote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_4', 3, NULL, 'assets/topic/asb/13338.png', 'Ilikuwa siku ya soko. Mamboleo aliomba miguu kutoka kwa mwanamume mmoja, mikono kutoka kwa mwingine, na kiwiliwili kutoka kwa mtu tofauti. Kisha Mamboleo akaziunganisha sehemu hizo tofauti. Akakiweka kichwa chake juu yake halafu akaenda sokoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_5', 3, NULL, 'assets/topic/asb/13339.png', 'Mamboleo alimtaka Adun na alikuwa na azma ya kumuoa. Adun alipendezwa na Mamboleo akataka kuolewa naye. Ingawa Mamboleo alitoka mji wa mbali, Adun alikuwa tayari kurudi naye kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_6', 3, NULL, 'assets/topic/asb/13340.png', 'Adun na Mamboleo walipokuwa wakisafiri, mwenye miguu aliichukua. Kisha mwenye mikono akaichukua mikono yake. Mwishowe kabisa, mwenye kiwiliwili alikichukua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_7', 3, NULL, 'assets/topic/asb/13341.png', 'Kichwa pekee kilibaki kikaendelea kutembea na Adun. Adun aliogopa lakini hakutoroka. Baadaye, waliwasili katika nyumba ya kichwa hicho.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_8', 3, NULL, 'assets/topic/asb/13342.png', 'Keshoye kabla ya kichwa kuondoka kwenda shambani,  kilimweleza Kobe, "Adun akijaribu kutroroka, ipulize pembe hii unijulishe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_9', 3, NULL, 'assets/topic/asb/13343.png', 'Kichwa kilipoondoka, Adun alifunganya virago vyake akitaka kutoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_10', 3, NULL, 'assets/topic/asb/13344.png', 'Kobe alipoona hivyo, aliipuliza pembe akisema,  "Kichwa, kichwa, Adun yu karibu kutoroka."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_11', 3, NULL, 'assets/topic/asb/13345.png', 'Kichwa kilimwendea Adun na kusema, "Unadhani unaenda wapi?" Adun alirejea nyumbani kwa kichwa,  shingo upande.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_12', 3, NULL, 'assets/topic/asb/13346.png', 'Hatimaye, Adun alitafuta mawaidha kutoka kwa mganga. Mganga alimshauri, "Nenda ununue keki za maharage. Ziloe katika mafuta kisha uziweke ndani ya ile pembe anayoipuliza."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_13', 3, NULL, 'assets/topic/asb/13347.png', 'Adun alifuata maagizo ya mganga. Aliziweka keki zilizoloa mafuta ndani ya ile pembe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_14', 3, NULL, 'assets/topic/asb/13349.png', 'Asubuhi ilipofika, Adun alivichukua virago vyake akaanza kutoroka tena. Kobe alipoichukua pembe kuipuliza, keki tamu zilianguka mdomoni kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18218_15', 3, NULL, 'assets/topic/asb/13348.png', 'Kobe alizila na kuzila zile keki. Adun alitoroka akaenda zake.', NULL);
