INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_4', 3, NULL, 'assets/topic/asb/12872.png', 'Mimi na Chuma tunapendana sana. Tukitumwa,  tunaenda pamoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_5', 3, NULL, 'assets/topic/asb/12873.png', 'Siku moja nilitumwa na bibi dukani nikanunue chumvi na mafuta. Nilienda na Chuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_6', 3, NULL, 'assets/topic/asb/12874.png', 'Tukiwa njiani, tuliwaona watoto wenzetu wakicheza mpira wa miguu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_7', 3, NULL, 'assets/topic/asb/12876.png', 'Mimi nilitamani sana kucheza mpira huo. Nilisema,  "Ah! Mpira!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_8', 3, NULL, 'assets/topic/asb/12877.png', 'Nilimwambia Chuma, "Twende tuwaombe kucheza nao mpira. Mimi hupenda mpira wa miguu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_9', 3, NULL, 'assets/topic/asb/12878.png', 'Chuma alinijibu, "Twende kwanza dukani, halafu turudi kucheza."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_10', 3, NULL, 'assets/topic/asb/12879.png', 'Lakini, nilimwambia Chuma, "Tucheze kwanza wewe! Tukienda nyumbani bibi hataturuhusu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_11', 3, NULL, 'assets/topic/asb/12880.png', 'Tulikubaliwa na wenzetu tukacheza mpira. Nilikuwa golikipa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_12', 3, NULL, 'assets/topic/asb/12881.png', 'Nilijitahidi kudaka mpira. Wenzangu hawakuweza kufunga goli hata moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_13', 3, NULL, 'assets/topic/asb/12882.png', 'Uwanja tuliochezea ulikuwa na mchanga mwingi. Magoli yake yalikuwa mawe makubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_14', 3, NULL, 'assets/topic/asb/12883.png', 'Baada ya mchezo, tuliondoka kwenda dukani, lakini, hatukuwa na pesa. Nilianza kulia. Chuma alinikemea,  "Acha kulia. Ni wewe ulitaka tucheze kabla ya kwenda dukani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_15', 3, NULL, 'assets/topic/asb/12884.png', 'Tulirudi nyumbani bila chumvi wala mafuta. Pia, tulikuwa tumechafuka kweli kweli.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_16', 3, NULL, 'assets/topic/asb/12885.png', 'Tulimkuta bibi amekasirika mno. Alituuliza, "Mmekuwa wapi muda huu wote?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_17', 3, NULL, 'assets/topic/asb/12886.png', 'Alipogundua kuwa tulipoteza hela, alisema,  "Nawasamehe leo, lakini, msirudie tena kucheza bila ruhusa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12925_18', 3, NULL, 'assets/topic/asb/12887.png', 'Tulipoona tumesamehewa, tulienda haraka kuwalisha kuku na bata. Baadaye, tulioga na kuwa wasafi.', NULL);
