INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094', 2, 'Akadeli abahatika', 'assets/topic/asb/19016.png', 'Simon Ipoo
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_3', 3, NULL, 'assets/topic/asb/19004.png', 'Siku moja Akadeli, Lucia, Acharait na Maria walienda msituni kutafuta matunda. Walishikana mikono wakavuka mto mkubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_4', 3, NULL, 'assets/topic/asb/19005.png', 'Waliufikia mti uliojaa matunda mabivu. Walikubaliana kuukwea mti ule na kuyachuma matunda wakiwa wameyafumba macho yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_5', 3, NULL, 'assets/topic/asb/19007.png', 'Hata hivyo, Lucia, Acharait na Maria hawakuyafumba macho yao. Akadeli pekee alifanya hivyo alipokuwa akichuma matunda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_6', 3, NULL, 'assets/topic/asb/19009.png', 'Ilipofika wakati wa kuyafumbua macho, Akadeli alikuta kwamba alikuwa ameyachuma matunda mabichi pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_7', 3, NULL, 'assets/topic/asb/19010.png', 'Lucia, Acharait na Maria walimcheka Akadeli kisha wakaondoka kwenda zao nyumbani. Akadeli aliyatupa matunda yale mabichi akaanza kuchuma mabivu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_8', 3, NULL, 'assets/topic/asb/19011.png', 'Muda mfupi baadaye, Akadeli alikijaza kikapu chake kwa matudna mabivu. Alianza kuuvuka mto ule mkubwa peke yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_9', 3, NULL, 'assets/topic/asb/19012.png', 'Akadeli alipokuwa katikati, kikapu chake kilimponyoka na kuanguka majini. Alikasirika sana akaanza kulia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_10', 3, NULL, 'assets/topic/asb/19013.png', 'Kabla hajavuka ng''ambo, Akadeli alimwona samaki ukingoni mwa mto. Alimchukua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_11', 3, NULL, 'assets/topic/asb/19014.png', 'Alipokuwa amembeba samaki yule, mwewe alimnyang''anya na kuruka naye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_12', 3, NULL, 'assets/topic/asb/19015.png', 'Akadeli aliona unyoya mmoja mrefu uliokuwa umeachwa na mwewe. Ingawa alikasirika, aliuchukua ule unyoya na kuendelea na safari yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_13', 3, NULL, 'assets/topic/asb/19016.png', 'Akadeli aliwapita watu wakisherehekea harusi ya kitamaduni. Wachezaji walivalia nyasi vichwani badala ya manyoya kama ilivyokuwa desturi. Waliuchukua ule unyoya wakampatia fahali mkubwa!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19094_14', 3, NULL, 'assets/topic/asb/19017.png', 'Akadeli alifika nyumbani na fahali wake. Wazazi na jamaa zake walifurahi sana. Lucia, Acharait na Maria walisikitaka kumdanganya Akadeli. Ilikuwa siku ya bahati kwa Akadeli!', NULL);
