INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_3', 3, NULL, 'assets/topic/asb/9494.png', 'Akai hakuwa na viatu. Marafiki zake walivaa viatu vizuri walipocheza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_4', 3, NULL, 'assets/topic/asb/9492.png', 'Akai alimwomba mamake amnunulie viatu. Ilikuwa shida kucheza bila viatu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_5', 3, NULL, 'assets/topic/asb/9493.png', 'Walipotembea kijijini, mama alitambua kuwa watoto wengine walivaa viatu isipokuwa Akai na Etabo.', NULL);
