INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_9', 3, NULL, 'assets/topic/asb/9499.png', 'Usiku ule, Akai hakuweza kulala. Aliwazia tu viatu vyake vipya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_10', 3, NULL, 'assets/topic/asb/10444.png', 'Hamu ya kuvaa viatu ilizidi. Akai akasimama na kuvivaa viatu vyake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_11', 3, NULL, 'assets/topic/asb/9498.png', 'Hata baada ya kurudi kitandani, Akai aliwaza jinsi angewaonyesha wenzake viatu vyake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15862_12', 3, NULL, 'assets/topic/asb/9502.png', 'Akai aliamka tena na kuvivaa viatu vyake vipya. Alicheza chumbani.', NULL);
