INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908', 2, 'Amara na wanyama', 'assets/topic/asb/14042.png', 'John Musene
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_3', 3, NULL, 'assets/topic/asb/14037.png', 'Amara alikuwa msichana wamiyaka mitisaa . Alisoma chuo cha Kerema. Alipenda misomo mbali mbali zilizomo kingereza, sayansi na pia masomo ya kijamii. Alitaka kuwa mwanasheria wakati yeye kukua. Amara aliwapenda wanyama sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_4', 3, NULL, 'assets/topic/asb/14113.png', 'Amara ali ishi na mama yake Margy. Margy alikuwa mukulima mwenye bidi. Baba wake Amara ali iaga dunia Amara akiwa na mwiaka mitano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_5', 3, NULL, 'assets/topic/asb/14040.png', 'Ambapo Amara akuwa shuleni, alishinda na Mbisa, mbuzi. Alimuwosha na kumulisha pia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_6', 3, NULL, 'assets/topic/asb/14039.png', 'Amara alikuwa na kipenzi chake, Simba. Mama yake Amara akupenda umbwa sana, ingapo alimuwacha Amara amuweke Simba. Wakati wowote Simba alimusumbuwa, alipiga kelele "nenda kabla nikupige teke." Amara aliuzunika kwa hili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_7', 3, NULL, 'assets/topic/asb/14062.png', 'Juma mosi moja, walikuwa na wageni kutoka kikundi cha wanawake. Amara alikuwa mwenye fura kuwa marafiki wamam yake walikuja na watoto wao. Wange cheza mpira.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_8', 3, NULL, 'assets/topic/asb/13930.png', 'Mama yake alikipika chakula kikundi kilicho penda. Amara alikumbushwa na mama yake kunfunga Simba. Alibweka kwa wageni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_9', 3, NULL, NULL, 'Amara hakunfunza Simba vizuri. Wageni walishanga kumuona mbwa kwenye chumba. Mama yake Oto aliuliza, "mbona mwakubali umbwa kwenye nyumba?" Mama yake Amara alimuita apeleke Simba inje.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_10', 3, NULL, 'assets/topic/asb/14055.png', 'Amara hakumskia mama yake kwamana walikuwa kwenye mijezo', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_11', 3, NULL, 'assets/topic/asb/14042.png', 'Ambapo Amara akuwa shuleni, alishinda na Mbisa, mbuzi. Alimuwosha na kumulisha pia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23908_12', 3, NULL, 'assets/topic/asb/13697.png', 'Baadaye, mama yake Amara alimuomba msamaha kwakuwa kikatili kwa Simba. Amara alikuwa mwenye furaha kwa kuwa mama yake alimukubali umbwa mara ya mwisho. Amara akiwa shuleni mama yake umlisha Simba. Marafiki wa mama wake Amara wame jifunza kuwa wazuri kwenye umbwa wao.', NULL);
