INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978', 2, 'Anansi awapa watu hadithi', 'assets/topic/asb/1294.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_3', 3, NULL, 'assets/topic/asb/1281.png', 'Hapo zamani za kale, Mungu wa Mbinguni, Nyame,  alizifungia hadithi zote kwenye sanduku la mbao, huko juu. Watu duniani walihuzunika sana kwa kuwa hawakuhadithiana. Wakamwuliza Anansi, buibui, mwerevu, awasaidie.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_4', 3, NULL, 'assets/topic/asb/1282.png', 'Anansi alisokota utando mrefu unaonata, akautumia kupanda hadi mbinguni. "Tafadhali nipe hadithi?" Anansi alimwambia Mungu wa mbinguni. Lakini Nyame alimcheka Anansi akisema, "Hadithi hizi ni ghali mno. Hutaweza kuzilipia, buibui mdogo, wee."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_5', 3, NULL, 'assets/topic/asb/1283.png', '"Hadithi hugharimu pesa ngapi?" Anansi aliuliza." "Itabidi uniletee wanyama watatu wakali tena wa kipekee", Nyame alijibu. "Chui mwenye meno makali kama mkuki, nyigu anayewauma watu, na nyoka anayewameza watu." Alicheka tena. Alidhani kwamba hadithi zake zilikuwa salama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_6', 3, NULL, 'assets/topic/asb/1284.png', 'Anansi alishuka chini polepole. Aliwaza na kuwazua kisha akapata mpango. Alichimba shimo refu, akalifunika kwa matawi na udongo ili lisionekane, kisha akaenda nyumbani. Asubuhi iliyofuata, chui alikuwa ameanguka kwenye lile shimo. Alikuwa akikwaruza kuta za shimo kwa ghadahbu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_7', 3, NULL, 'assets/topic/asb/1286.png', '"Hebu nikusaidie, rafiki yangu." Anansi alisema." "Lala juu ya vijiti hivi halafu nitakuvuta utoke nje." Anansi akautungia utando wake unaonata kwenye mwili wa chui, na vijiti, kisha akamvuta hadi juu kwa Mungu wa Mbinguni. Lakini, Nyame alimcheka akauliza, "Nyigu na nyoka wako wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_8', 3, NULL, 'assets/topic/asb/1287.png', 'Anansi alirudi tena ardhini kumtafuta mnyama wa pili. Alifikiri hatimaye akapata wazo. Alichukua kibuyu kilichojaa maji na kwenda kwenye mti walimoishi nyigu. Akamwagilia nyumba ya nyigu maji mpaka ikalowa kabisa. Kisha akatafuta jani la mgomba akajikinga nalo kichwani. Akajimwagilia maji yaliyobaki kila sehemu ya mwili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_9', 3, NULL, 'assets/topic/asb/1288.png', 'Halafu akawaita manyigu, "Aisee nyigu! Njooni mwone! Mvua inanyesha! Ingieni haraka ndani ya kibuyu changu ili mjikinge na mvua." Nyigu hawapendi kulowa maji. Walikimbilia ndani ya kibuyu cha Anansi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_10', 3, NULL, 'assets/topic/asb/1289.png', 'Kwa haraka, Anansi aliutungia utando wake kwenye mlango ili nyigu wasitoke kamwe. Akawabeba wote hadi juu kwa Mungu wa Mbinguni. Lakini Nyame alisema, "Wapi nyoka?" Wakati huu hakucheka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_11', 3, NULL, 'assets/topic/asb/1291.png', 'Kwa hivyo, Anansi alirudi ardhini tena. Alifikiri lakini hakuna wazo zuri lililomjia akilini. Basi akamwuliza mkewe aliyekuwa na wazo bora sana. Walishirikiana kutafuta kijiti kimoja kirefu na kamba dhabiti. Walipofika karibu na mto alimoishi nyoka, walianza kubishana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_12', 3, NULL, 'assets/topic/asb/1292.png', 'Mmoja alidai kuwa kijiti kile kilikuwa kirefu huku mwingine akisema sivyo. Nyoka alitoka majini akawauliza sababu ya mabishano yao. "Mke wangu anasema kwamba kijiti hiki ni kirefu kukuliko wewe. Lakini si kweli," Anansi alisema. "Mimi ni mrefu kuliko kijiti hicho! Hebu kiweka karibu nami nijipime," nyoka alijibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_13', 3, NULL, 'assets/topic/asb/1293.png', 'Anansi alifanya hivyo. Kisha alimfunga yule nyoka kwenye kijiti kile kwa kutumia kamba ili kumnyoosha sawasawa. Alimbeba hadi juu mbinguni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('1978_14', 3, NULL, 'assets/topic/asb/1294.png', 'Nyame akaridhika kwamba Anansi alikuwa ametoa malipo ya kutosha. Basi akafungua sanduku lake la mbao, akampa Anansi hadithi. Anansi alizibeba hadithi hizo kwa ushindi mkubwa hadi ardhini. Akazigawa kwa mkewe, watu pamoja na wanyama.  Hadithi ni za kuhadithia wala si za kuficha sandukuni.', NULL);
