INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234', 2, 'Anansi awapa watu hekima', 'assets/topic/asb/1273.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_3', 3, NULL, 'assets/topic/asb/1274.png', 'Hapo zamani za kale, watu hawakujua chochote. Hawakujua kupanda mimea, kushona nguo, wala kuuunda vyombo vya chuma. Lakini mungu aliyeitwa Nyame akiwa mbinguni, alimiliki hekima ambayo watu walihitaji. Nyame alihifadhi hekima hiyo kwenye chungu cha udongo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_4', 3, NULL, 'assets/topic/asb/1275.png', 'Siku moja, Nyame aliamua kumpa Anansi chungu hicho kilichojaa ujuzi na hekima. Anansi alikitazama chungu kile. Na kila alipokitazama alipata kujua jambo jipya. Alifuarahi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_5', 3, NULL, 'assets/topic/asb/1276.png', 'Anansi alisema moyoni, "Nitakificha chungu hiki juu ya mti mrefu ili mtu mwingine asikifikie. Ningetaka kufaidika peke yangu." Kwa kutumia miyaa, Anansi alitengeneza kamba akaifungia kwenye chungu. Kisha, alijifungia kamba hiyo kiunoni. Aliupanda mti ule huku kile chungu kikining''inia mbele yake. Haikuwa rahisi kwani kila wakati, chungu kile kilimgonga magotini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_6', 3, NULL, 'assets/topic/asb/1277.png', 'Wakati huo wote, mwanawe Anansi alikuwa amesimama chini ya ule mti akimtazama babake. Hatimaye, alimwambia, "Baba, mbona usikifunge chungu mgongoni ili uweze kuuparaga mti?" Anansi aliamua kukifunga mgongoni chungu hicho kilichojaa hekima. Ilikuwa rahisi zaidi kukibeba kile chungu mgongoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_7', 3, NULL, 'assets/topic/asb/1278.png', 'Baada ya muda mfupi, alifika juu ya mti. Akiwa juu aliwaza, "Mimi ndiye ninabeba hekima lakini,  mwanangu anaonekana kuwa mwerevu kuniliko." Anansi alikasirika na kukitupa kile chungu chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2234_8', 3, NULL, 'assets/topic/asb/1279.png', 'Chungu kilianguka na kuvunjika vigae vingi. Watu waliipata hekima wakajua kulima, kushona nguo, na kuunda vyombo vya chuma. Hivyo ndivyo watu walivyojua kufanya mambo yote wanayofanya.', NULL);
