INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_3', 3, NULL, 'assets/topic/asb/27118.png', 'Hapo zamani, kulikuwa na mvulana aliyeitwa Jalo. Jalo aliwaasi wazazi wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_4', 3, NULL, 'assets/topic/asb/27119.png', 'Siku moja Jalo na mamake waliwatembelea jamaa zao kijijini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_5', 3, NULL, 'assets/topic/asb/27120.png', 'Walimpeleka Jalo na mamake kwenye bustani iliyojaa matunda. Kisha wakawaalika kula matunda yoyote waliyopenda.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_6', 3, NULL, 'assets/topic/asb/27121.png', 'Jalo aliona mwembe mkubwa uliokuwa na maembe mengi mabivu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_7', 3, NULL, 'assets/topic/asb/27122.png', 'Jalo alishangaa kuyaona maembe mengi mabivu. Aliamua kuchuma moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_8', 3, NULL, 'assets/topic/asb/27123.png', 'Ingawa Jalo alikuwa ameonywa kutokula kutoka kwenye mwembe, alifanya hivyo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_9', 3, NULL, 'assets/topic/asb/27124.png', 'Mamake Jalo alimwonya tena. Hata hivyo, Jalo hakumtii.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_10', 3, NULL, 'assets/topic/asb/27125.png', 'Alipolila tu hilo embe, tumbo ilianza kunguruma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_11', 3, NULL, 'assets/topic/asb/27126.png', 'Jalo aliendelea kuhisi vibaya akaanza kulia. Kichwa chake kilianza kuvimba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_12', 3, NULL, 'assets/topic/asb/27127.png', 'Halafu, mwembe uliota kichwani kwake! Jalo aligeuka na kuwa mwembe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_13', 3, NULL, 'assets/topic/asb/27128.png', 'Tangu wakati huo, kila mwembe huo unapoguswa, huimba, "Asiyewatii wazee, atajuta. Jalo ni mfano."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24734_14', 3, NULL, NULL, 'Hivyo ndivyo ilivyo. Wahenga husema, "Asiyesikia la mkuu, huvunjika guu."', NULL);
