INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428', 2, 'Basi kubwa la bluu', 'assets/topic/asb/11446.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_3', 3, NULL, 'assets/topic/asb/11446.png', 'Kulikuwa na basi moja tu katika kijiji cha Ebei. Lilikuwa basi kubwa lenye rangi ya bluu. Lilinguruma sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_4', 3, NULL, 'assets/topic/asb/11447.png', 'Siku moja mamake Ebei alisema, "Kesho tutaenda mjini kununua sare yako ya shule."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_5', 3, NULL, 'assets/topic/asb/11448.png', 'Ebei alifurahi sana. Kumbe atasafiri katika lile basi kubwa la bluu! Usiku huo hakuweza kulala vizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_6', 3, NULL, 'assets/topic/asb/11449.png', 'Mamake alipoenda kumuamsha, Ebei alikuwa tayari amevaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_7', 3, NULL, 'assets/topic/asb/11450.png', 'Ebei na mamake walienda kwenye kituo cha basi. Walisubiri lile basi kubwa la bluu. Basi halikufika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_8', 3, NULL, 'assets/topic/asb/11451.png', 'Watu wengine walifika kwenye kituo cha basi. Wote walilalamika kuwa basi lilichelewa. Mmoja aliuliza, "Basi limetuacha?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_9', 3, NULL, 'assets/topic/asb/11452.png', 'Ebei alikuwa na wasiswasi. "Tusipokwenda mjini,  sitapata sare yangu ya shule," aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_10', 3, NULL, 'assets/topic/asb/11453.png', 'Baadhi yao walikata tamaa wakarudi nyumbani. Lakini Ebei alilia akakataa kurudi. "Tutasubiri zaidi," mamake alimtuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_11', 3, NULL, 'assets/topic/asb/11454.png', 'Ghafla, walisikia mngurumo. Wakaona vumbi hewani. Basi lilikuwa linakuja kwa kasi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_12', 3, NULL, 'assets/topic/asb/11455.png', 'Basi halikuwa la bluu wala halikuwa kubwa. Basi lile lilikuwa dogo na jekundu. Waliosubiri waliliangalia tu na hawakuingia ndani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_13', 3, NULL, 'assets/topic/asb/11456.png', 'Dereva aliwaita, "Ingieni! Ingieni!
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_14', 3, NULL, 'assets/topic/asb/11457.png', 'Ebei na mamake walikuwa wa kwanza kuingia. Mara wote waliingia ndani ya basi lile dogo jekundu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_15', 3, NULL, 'assets/topic/asb/11458.png', 'Ebei alichungulia dirishani. Aliwaona watu wengi zaidi wakiingia ndani ya basi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_16', 3, NULL, 'assets/topic/asb/11459.png', 'Mara basi likaondoka. Ebei aliwaona watu wengine wakikimbia kulifikia basi. Walikuwa tayari wamechelewa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_17', 3, NULL, 'assets/topic/asb/11460.png', 'Mamake Ebei alimuuliza dereva, "Liko wapi basi kubwa la bluu?" Dereva alimjibu, "Samahani mama,  basi la bluu liliharibika. Linatengenezwa. Kesho litakuja."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12428_18', 3, NULL, 'assets/topic/asb/11461.png', 'Ebei hakujali rangi wala ukubwa wa basi. Alifurahi kuwa basi lile lilikuwa linawapeleka mjini. Hatimaye,  atanunuliwa sare ya shule.', NULL);
