INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419', 2, 'Bi Simu', 'assets/topic/asb/10053.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_3', 3, NULL, 'assets/topic/asb/10048.png', 'Huyu anaitwa Bi Simu. Mtazame vizuri!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_4', 3, NULL, 'assets/topic/asb/10049.png', 'Bi Simu ana simu tano. Akiamka, yeye huzipanga simu zake tano mezani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_5', 3, NULL, 'assets/topic/asb/10050.png', 'Kila simu ina kazi tofauti. Simu ya rangi ya manjano ni ya kuwasiliana na familia yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_6', 3, NULL, 'assets/topic/asb/10051.png', 'Nayo ile ya rangi nyeupe, ni ya kuwasiliana na wenzake anaofanya nao kazi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_7', 3, NULL, 'assets/topic/asb/10052.png', 'Bi Simu huitumia ile ya rangi nyekundu kuwasiliana na watu anaowadai!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_8', 3, NULL, 'assets/topic/asb/10053.png', 'Ile ya rangi ya waridi ni ya kuwasiliana na marafiki zake wa dhati.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_9', 3, NULL, 'assets/topic/asb/10054.png', 'Bi Simu huitumia ile ya kizambarau kuwasiliana na watu wanaohitaji msaada wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_10', 3, NULL, 'assets/topic/asb/10055.png', 'Bi Simu hupenda watu wafurahi. Hapendi ugomvi wala vita.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_11', 3, NULL, 'assets/topic/asb/10056.png', 'Asipokuwa kazini, Bi Simu huwaalika watoto ukumbini kwake. Wao hutazama runinga na kupata mawaidha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_12', 3, NULL, 'assets/topic/asb/10057.png', 'Bi Simu anawahurumia wanyonge. Huwatembelea wagonjwa na wazee nyumbani kwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_13', 3, NULL, 'assets/topic/asb/10058.png', 'Kila apitapo, watu hupiga mayowe wakimwita, "Bi Simu! Bi Simu!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14419_14', 3, NULL, 'assets/topic/asb/10059.png', 'Naye hufurahia na kusema, "Mimi ni Bi Simu. Nazitumia simu zangu kuwasiliana na wote."
