INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610', 2, 'Biantaka na chungu kilichokufa', 'assets/topic/asb/14436.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_3', 3, NULL, 'assets/topic/asb/14435.png', 'Hapo zamani za kale, kulikuwa na mwanamme aliyeitwa Biantaka. Alikuwa na ng''ombe. Kila siku Biantaka alimlisha ng''ombe wake na kumpa maji. Alikuwa na chungu kidogo alichotumia kuchota maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_4', 3, NULL, 'assets/topic/asb/14436.png', 'Siku moja, Biantaka alimwendea jirani yake kumuomba chungu kikubwa ili akitumie kuchota maji. Jirani yake alikubali kumwazima Biantaka chungu kikubwa. Alisema, "Tatizo la jirani yangu ni tatizo langu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_5', 3, NULL, 'assets/topic/asb/13193.png', 'Siku chache baadaye, Biantaka alienda kwa mfinyanzi akanunua chungu kidogo. Alipofika nyumbani, alikiweka ndani ya kile chungu kikubwa alichomuomba jirani yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_6', 3, NULL, 'assets/topic/asb/13194.png', 'Alijitwika chungu kikubwa kilichokuwa na kile kidogo ndani yake. Akaenda moja kwa moja hadi kwa jirani yake aliyemwazima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_7', 3, NULL, 'assets/topic/asb/14440.png', 'Biantaka alimwambia, "Nimekurejeshea chungu chako ambacho kimezaa kingine." Jirani alishangaa kwamba chungu chake kilikuwa kimezaa chungu kingine. Alimpongeza Biantaka akisema, "Umebarikiwa sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_8', 3, NULL, 'assets/topic/asb/14437.png', 'Baada ya muda, Biantaka alirudi kwa jirani yake kumuomba chungu tena. Mara hii, kakuwa na nia nzuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_9', 3, NULL, 'assets/topic/asb/14447.png', 'Mwenye chungu alimsubiri Biantaka arejeshe chungu chake bila mafanikio. Mwishowe, alikwenda nyumbani kwa Biantaka akasema, "Nimekuja kukichukuwa chungu changu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_10', 3, NULL, 'assets/topic/asb/14446.png', 'Biantaka alimwambia jirani yake, "Rafiki yangu, chungu chetu kilifariki. Nilikusudia kuja kukuletea ujumbe huo mbaya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_11', 3, NULL, 'assets/topic/asb/14448.png', 'Kwa mshangao, Jirani aliruka juu. Uso wake ulianza kufura kwa hasira. Alipiga kelele akimkemea Biantaka, "Sijawahi kusikia kuwa chungu kinafariki!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_12', 3, NULL, 'assets/topic/asb/14450.png', 'Biantaka alimjibu, "Rafiki yangu, lazima uyakubali mambo haya. Kila kitu kinachozaa lazima pia kifariki. Mimi pia nilihuzunika sana kufuatia kifo cha chungu chako kikubwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_13', 3, NULL, 'assets/topic/asb/14449.png', 'Jirani alifoka kwa hasira akaenda kortini kumshtaki Biantaka. Hakimu alisikiliza maelezo ya pande zote mbili. Aliamua kuwa jirani mwenye chungu ndiye aliyekuwa na makosa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_14', 3, NULL, 'assets/topic/asb/14451.png', 'Hakimu alimwuliza, "Biantaka alipokwambia kuwa chungu chako kilizaa, ulikubali. Kwa hivyo unapaswa kukubali anaposema kwamba kila kiumbe hai kinachozaa lazima kifariki kwa kuwa anasema ukweli." Hivyo ndivyo hakimu alivyoamua kesi hiyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21610_15', 3, NULL, 'assets/topic/asb/14452.png', 'Jirani alitembea polepole kama konokono akarudi nyumbani. Alikuwa amekipoteza chungu chake kikubwa. Naye Biantaka alikuwa amekipata chungu kikubwa kwa kutumia ujanja wake.', NULL);
