INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503', 2, 'Biashara ya Akoro ya ndizi', 'assets/topic/asb/13467.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_3', 3, NULL, 'assets/topic/asb/10070.png', 'Akoro ni mumewe Chichi. Wanaishi katika kijiji kiitwacho Kanam katika Kaunti ya Turkana. Akoro ana bustani ya ndizi inayompatia mazao mazuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_4', 3, NULL, 'assets/topic/asb/13459.png', 'Siku moja kabla jua kuwa kali, Akoro aliondoka akiwa amebeba kreti ya ndizi mbivu kichwani kwake. Alitembea kwa ujasiri akielekea sokoni Kilindo, umbali wa kilomita moja kutoka nyumbani kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_5', 3, NULL, 'assets/topic/asb/13460.png', 'Njiani, wanaume na wanawake wa umri wake walimtazama kwa mshangao. Baadhi yao hata walimcheka kwa sauti, "Bila shaka Akoro amerogwa. Anawezaje kufanya kazi kama hii? Wanaume kutoka jamii yetu hawauzi ndizi!" Lakini, Akoro hakujali. Alikuwa ameamua na angefanya kwa bidii kazi yoyote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_6', 3, NULL, 'assets/topic/asb/13462.png', 'Alipowasili sokoni tu, Akoro alizipanga ndizi zake kisha akaketi karibu nazo akisubiri wateja. Alijiuliza, "Je, kuna yeyote atakayezinunua ndizi zangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_7', 3, NULL, 'assets/topic/asb/13466.png', 'Dakika chache baadaye, mwanamke aliyevalia rinda la  kizambarau, alisimama karibu na kibanda cha Akoro. "Ndizi kilo moja inagharimu pesa ngapi?" mwanamke yule aliuliza. "Shilingi hamsini, dadangu," Akoro alimjibu kwa heshima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_8', 3, NULL, 'assets/topic/asb/13465.png', 'Mwanamke yule akasema, "Tafadhali nifungie kilo moja na nusu." Aliendelea kusema, "Lazima unipunguzie bei." Akoro akamjibu, "Hakuna shida dadangu, nitapunguza shilingi kumi. Wewe ni mteja wangu wa kwanza." Akoro alimpatia mwanamke yule ndizi zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_9', 3, NULL, 'assets/topic/asb/13464.png', 'Akoro alianza kufurahia biashara yake. Wanunuzi walivutiwa na ndizi zilizopendeza na maneno ya Akoro matamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_10', 3, NULL, 'assets/topic/asb/13463.png', 'Ilipofika adhuhuri, Akoro alikuwa amechoka kusimama. Alitafuta kigoda akakalia akaendelea kuuza ndizi zake. Mwanamke aliyevalia rinda la bluu, alinunua ndizi nyingi. Waliomcheka Akoro, walianza kushangaa jinsi biashara yake ya ndizi ilivyonawiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_11', 3, NULL, 'assets/topic/asb/13468.png', 'Ilipotimia saa tisa alasiri, Akoro alijitayarisha kuuza ndizi zake za mwisho. Aliwapa ndizi mbili mbili wanaume walioketi naye wakati huo wote. Walimtumbuiza kwa hadithi tamu alipowasubiri wateja. Akoro aliuza ndizi za mwisho kwa mwanamke mmoja aliyekuwa akienda nyumbani kutoka kazini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_12', 3, NULL, 'assets/topic/asb/13470.png', 'Jioni, Akoro alikwenda nyumbani akiwa amebeba kreti tupu kichwani kwake. Alipotembea, aliimba wimbo wa furaha huku akijivunia kazi aliyokuwa amefanya siku hiyo. Aliwaza, "Kufanya bidii kwa dhati ni jambo muhimu maishani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31503_13', 3, NULL, 'assets/topic/asb/13469.png', 'Alipokaribia nyumbani, Akoro aliinunulia familiia yake unga wa mahindi, mafuta ya mboga, chai, sukari na mkate. Kisha alienda nyumbani kwa furaha. Mawazo yake yalijaa mipango ya kuipanua biashara yake ya ndizi.', NULL);
