INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110', 2, 'Bibi atusimulia hadithi', 'assets/topic/asb/18842.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_3', 3, NULL, 'assets/topic/asb/18819.png', 'Sote tulikuwa tumekusanyika nyumbani kwa bibi kuzisikiliza hadithi zake. Usiku huo, tulikuwa tumeketi karibu na meko bibi alipoanza kutusimulia hadithi:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_4', 3, NULL, 'assets/topic/asb/18831.png', 'Sungura alikuwa akipenda uyoga, lakini alikuwa mvivu sana. Alizoea kuiba uyoga wa Tembo. Mwaka huo, Tembo alipanda ndizi na maboga. Kwa hivyo, Sungura hakupata uyoga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_5', 3, NULL, 'assets/topic/asb/18832.png', 'Sungura alikosa chakula kwa siku nyingi akaamua kuiba ndizi za Tembo. Tembo aligundua kwamba ndizi zake zilikuwa zikiibwa. Alimwendea Sungura na kusema, "Kuna mtu fulani ananiibia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_6', 3, NULL, 'assets/topic/asb/18833.png', 'Sungura aliuliza, "Anaweza kuwa nani anayekuibia?" Tembo alianza kuimba, "Nitamshika mwizi! Aaa! Nitamshika mwizi! Ooo!" Sungura alikuwa na wasiwasi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_7', 3, NULL, 'assets/topic/asb/18834.png', 'Tembo alimwuliza Kima kumchungia shamba lake. Lakini, Sungura hakuiba mchana wala usiku huo. Hakuiba siku iliyofuata wala usiku uliofuata. Kima alichoka kuchunga akarudi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_8', 3, NULL, 'assets/topic/asb/18835.png', 'Siku iliyofuata, Sungura alienda shambani kwa Tembo kuiba tena. Tembo alipoenda kukagua mimea yake,  Sungura alijificha katika matawi ya maboga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_9', 3, NULL, 'assets/topic/asb/18836.png', 'Tembo aliyaona matawi yakitingika akaanza kuimba, "Nimemshika mwizi! Aaa! Nimemshika mwizi! Ooo!" Tembo alitafuta kila mahali lakini hakumpata mwizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_10', 3, NULL, 'assets/topic/asb/18837.png', 'Siku moja, Sungura alipokuwa akila mazao ya Tembo shambani, Tembo aliwasili. Sungura aliruka na kujificha ndani ya boga moja kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_11', 3, NULL, 'assets/topic/asb/18839.png', 'Tembo aliliona lile boga kubwa akasema, "Ooo! Hapa kuna boga kubwa. Litakuwa chakula changu cha mchana." Alilimeza papo hapo!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_12', 3, NULL, 'assets/topic/asb/18840.png', 'Sungura alijaribu kuruka kutoka ndani ya lile boga, lakini hakuweza. Tembo alipohisi kitu kikiruka tumboni mwake, alijiuliza, "Hili ni boga aina gani linaloruka tumboni mwangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_13', 3, NULL, 'assets/topic/asb/18841.png', 'Tembo alilitema nje lile boga. Kabla kulikagua,  Sungura alijitosa nje ya lile boga akatoroka. Tembo hakuwahi kumshika mwizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19110_14', 3, NULL, 'assets/topic/asb/18842.png', 'Bibi alisema, "Na huo ndio mwisho wa hadithi." "Asante bibi," tulimjibu pamoja. Tuliviweka vikombe vyetu vya maziwa, kisha tukaenda kulala tukiwaza juu ya hadithi hiyo ya bibi.', NULL);
