INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899', 2, 'Binti wa Mflame Kayanja', 'assets/topic/asb/10069.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_3', 3, NULL, 'assets/topic/asb/10074.png', 'Hapo zamani za kale, aliishi Mfalme kwa jina Kayanja. Waliishi katika kasri kubwa yeye, mkewe malkia na Apenyo binti yao. Apenyo alikuwa msichana mrembo. Kila mwanmme alitaka kumwoa,  lakini mfalme Kayanja alitaka mahari ya juu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_4', 3, NULL, 'assets/topic/asb/10063.png', 'Karibu na kasri la mfalme Kayanja, aliishi Chifu Aludah Mkuu. Aliitwa ''Mkuu'' kwa sababu kila aliyeishi pale kijijini, alimtii. Chifu Aludah alikuwa amempoteza mkewe kutokana na ugonjwa wa malaria. Alitaka kuoa mke mwingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_5', 3, NULL, 'assets/topic/asb/10064.png', 'Chifu Aludah alikuwa mzee mfupi mwenye upara. Aliamua kupeleka mahari kwa Mfalme Kayanja ili amwoe bintiye. Walipokuwa wakijadili mahari,  kijakazi wa Mfalme, kwa jina Kakembo, aliyasikia mazungumzo yao. Kakembo alikuwa na uhusiano wa karibu sana na Apenyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_6', 3, NULL, 'assets/topic/asb/10065.png', 'Chifu Aludah alikubali kumpa Mfalme Kayanja nusu ya mali yake. Pia alileta sindano ambayo ilikuwa sehemu ya mahari aliyoleta. Matayarisho ya harusi yalifanywa kisisrisiri baina ya Mfalme Kayanja na Chifu Aludah. Mfalme Kayanja alijua kwamba bintiye hangefurahia uamuzi huo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_7', 3, NULL, 'assets/topic/asb/10066.png', 'Juma moja kabla ya siku ya harusi hiyo ya kifalme,  Kakembo alimwambia Apenyo, "Mpendwa binti-mfalme, babako amepanga kukuoza kwa Chifu Aludah. Harusi itafanyika katika muda wa juma moja."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_8', 3, NULL, 'assets/topic/asb/10067.png', 'Apenyo alishtuka asijue la kufanya. Aliwaza, "Kamwe sitaolewa na yule mzee mwenye upara. Kamwe! Lazima nimwone Trevor, mpenzi wangu, achukue hatua bila kupoteza muda."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_9', 3, NULL, 'assets/topic/asb/10069.png', 'Usiku huo Apenyo alitoroka. Babake angegundua alikokuwa akienda angekasirika sana. Alipitia kwenye msitu wa giza wenye mawe. Mwishowe, akafika kwa Trevor. Alipowasili nyumbani kwa Trevor, alikuwa amechoka, mwenye njaa na kiu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_10', 3, NULL, 'assets/topic/asb/10070.png', 'Trevor alimkaribisha. "Mpenzi wangu, mbona ukakimbia umbali huu peke yako usiku?" Trevor aliuliza. Alimletea maji ya kunywa kisha akatulia kumsubiri Apenyo aongee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_11', 3, NULL, 'assets/topic/asb/10071.png', 'Apenyo alikunywa maji, akashusha pumzi na kusema, "Babangu anataka kunioza kwa chifu Aludah Mkuu. Lakini, siwezi kuolewa na yule mzee mwovu. Trevor, ningependa kuolewa nawe hata kama wewe ni maskini. Ninakupenda."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_12', 3, NULL, 'assets/topic/asb/10072.png', '"Lakini sina chochote cha kulipia mahari." Trevor alilalama. Apenyo alijibu, "Naelewa, lakini nataka kuolewa na wewe tu." Aliwaza kwa muda kisha akasema, "Hebu twende kwa Kategga, mwenye mashua, atuvushe hadi upande mwingine wa mto tumuepuke babangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_13', 3, NULL, 'assets/topic/asb/10211.png', 'Mfalme Kayanja alipogundua kuwa bintiye ametoweka, aliamuru msako ufanywe kijijini. Walinzi na askari walimtafuta Apenyo kila mahali lakini hawakumpata. Walirudi na kumwambia Mfalme kwamba Apenyo hakuonekana popote. "Basi tafuteni pia vichakani," Mfalme Kayanja aliamrisha kwa hasira.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_14', 3, NULL, 'assets/topic/asb/10075.png', 'Trevor na Apenyo walipokuwa wakiharakisha kwenda mtoni, mawingu meusi yalianza kukusanyika angani. Dhoruba kubwa ilikuwa inakaribia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_15', 3, NULL, 'assets/topic/asb/10076.png', 'Kategga, mwenye mashua, alikuwa anafunga mashua yake Trevor na Apenyo walipofika. Trevor alimwmomba Kategga awavukishe hadi ng''ambo ya pili ya mto. Kategga alikataa akawaelezea kwamba dhoruba kali ilikuwa inakaribia na ingekuwa hatari kuvuka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_16', 3, NULL, 'assets/topic/asb/10077.png', 'Trevor alisizitiza huku akimwelezea kwa nini itawalazimu kuvuka  wakati huo. Alitia mkono mfukoni na kutoa kete yenye thamani na kumpa Kategga. Kategga aliwaonea huruma akakubali kuwavusha haraka kabla ya dhoruba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_17', 3, NULL, 'assets/topic/asb/10078.png', 'Kategga aliivuta mashua ukingoni. Wakaabiri na kuanza safari. Mfalme Kayanja na Chifu Aludah walipofika ufukweni, waliowaona watu watatu mashuani wakivuka. Wakatambua kwamba Apenyo alikuwa anatoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12899_18', 3, NULL, 'assets/topic/asb/10079.png', 'Dhoruba kali ilisababisha mawimbi makubwa. Kategga alishindwa kudhibiti mashua. Mfalme alilia, "Apenyo, tafadhali rudi! Nimewasamehe." Mayowe yake yalikuwa ya bure. Mashua ilipinduka wote wakazama. Tangu siku hiyo, walioishi katika ufalme wa Kayanja, walimwoa waliyemtaka, tajiri au maskini.', NULL);
