INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_3', 3, NULL, 'assets/topic/asb/17306.png', 'Mara nyingi, Chui alitaka kumshika Swara.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_4', 3, NULL, 'assets/topic/asb/17307.png', 'Swara naye alikuwa akitoroka kila alipomwona Chui.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_5', 3, NULL, 'assets/topic/asb/17309.png', 'Siku moja, Chui alimwambia Swara, "Ningependa tuwe marafiki. Unachokula, mimi sili. Hakuna sababu ya kuwa maadui."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_6', 3, NULL, 'assets/topic/asb/17310.png', 'Swara alikubali. Chui akasema, "Tule kiapo cha kutufanya marafiki. Atakayeasi, mtoto wake atakufa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_7', 3, NULL, 'assets/topic/asb/17311.png', 'Walikula kiapo cha kuwafanya marafiki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_8', 3, NULL, 'assets/topic/asb/17312.png', 'Baada ya muda, Swara alinona na Chui alikonda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_9', 3, NULL, 'assets/topic/asb/17313.png', 'Chui alitamani kumla Swara aliyenona. Akasema, "Sina haja na kiapo. Sina hata mtoto!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_10', 3, NULL, 'assets/topic/asb/17314.png', 'Chui alimrukia Swara akajaribu kumshika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_11', 3, NULL, 'assets/topic/asb/17315.png', 'Swara alishtuka sana. Aliruka juu akaanza kulia, "Bee! Bee!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_12', 3, NULL, 'assets/topic/asb/17316.png', 'Chui akamsihi, "Rafiki yangu, nisaidie. Tulikubaliana kuwa atakayevunja kiapo chetu, atampoteza mtoto wake."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22084_13', 3, NULL, 'assets/topic/asb/17317.png', 'Swara alimjibu, "Inaonekana kuwa wazazi wako ndio waliokula kiapo. Sasa wewe ndiwe utakayekufa."', NULL);
