INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434', 2, 'Chura na Nyoka', 'assets/topic/asb/18784.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_3', 3, NULL, 'assets/topic/asb/18783.png', 'Chura na Nyoka walikuwa marafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_4', 3, NULL, 'assets/topic/asb/18784.png', '"Njoo twende tukamtembelee mamangu." Chura alimwalika Nyoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_5', 3, NULL, 'assets/topic/asb/25000.png', '"Asante, lakini nani atatuchungia makao yetu?" Nyoka alimwuliza Chura.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_6', 3, NULL, 'assets/topic/asb/25001.png', '"Nitawachungia makao yenu," Kima alijitolea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_7', 3, NULL, 'assets/topic/asb/25002.png', 'Chura aliweka mchwa mkobani kumpelekea mamake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_8', 3, NULL, 'assets/topic/asb/25003.png', 'Chura na Nyoka waliondoka asubuhi wakaingia msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_9', 3, NULL, 'assets/topic/asb/25004.png', 'Waliwakuta vichakuro. Nyoka aliwaogopa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_10', 3, NULL, 'assets/topic/asb/25005.png', 'Chura alimlinda rafiki yake Nyoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_11', 3, NULL, 'assets/topic/asb/25006.png', 'Walipofika kwa mamake Chura, walikuwa wakihisi njaa sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_12', 3, NULL, 'assets/topic/asb/25007.png', 'Walinawa wakajitayarisha kwa chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_13', 3, NULL, 'assets/topic/asb/25008.png', '"Nyoka, tafadhali keti wima." Chura alimwambia rafikiye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_14', 3, NULL, 'assets/topic/asb/25009.png', 'Nyoka alijaribu kuketi wima, lakini hakuweza. Yeye si kama chura.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_15', 3, NULL, 'assets/topic/asb/25010.png', '"Siwezi kuketi wima kama chura," Nyoka alimjibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_16', 3, NULL, 'assets/topic/asb/25011.png', 'Chura hakumsikiza Nyoka. Alianza kukasirika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_17', 3, NULL, 'assets/topic/asb/25012.png', 'Nyoka pia alikasirika akasema, "Hunielewi. Heri nirudi nyumbani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20434_18', 3, NULL, 'assets/topic/asb/25013.png', 'Nyoka aliondoka kwa hasira. "Nilidhani Chura ni rafiki yangu." Aliwaza.', NULL);
