INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_4', 3, NULL, 'assets/topic/asb/7514.png', 'Mimi ndiye hufungua dirisha asubuhi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_5', 3, NULL, 'assets/topic/asb/7517.png', 'Mama huniambia, "Wewe ni nyota yangu ya asubuhi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_6', 3, NULL, 'assets/topic/asb/7507.png', 'Ninajiosha mwenyewe wala sihitaji usaidizi wowote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_7', 3, NULL, 'assets/topic/asb/7515.png', 'Sijali kuoga kwa maji baridi wala kutumia sabuni inayonuka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_8', 3, NULL, 'assets/topic/asb/7518.png', 'Mama akinikumbusha kupiga mswaki, ninamjibu, "Siwezi kusahau, mama!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_9', 3, NULL, 'assets/topic/asb/7516.png', 'Baada ya kuoga, mimi huwaamkua babu na shangazi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_10', 3, NULL, 'assets/topic/asb/7511.png', 'Huvaa nguo mwenyewe. "Nimekuwa mkubwa sasa," humwambia mama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_11', 3, NULL, 'assets/topic/asb/7508.png', 'Ninaweza kufunga vifungo vyangu na kukaza kamba za viatu vyangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_12', 3, NULL, 'assets/topic/asb/7512.png', 'Humhimiza ndugu yangu kwenda shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22086_13', 3, NULL, 'assets/topic/asb/7510.png', 'Hufanya bidii kwa kila njia nikiwa darasani.', NULL);
